import{_ as a}from"./chunks/index.VAya1KE4.js";import{c as p,o as e,G as s,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const r=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":500286,"idList":[504621,504570,504440,501214,503614,501157,501213,6489,3386,1058,1433,1056,1220,1498,503613,503612,4074,4073,500207,1290,502928,502927,5111,1627,502306,500987,501624,2404,501625,501626,2142,4334,6180,59,75,1057,501441,4081,500419,5020,500291,5865,500286,6937,500673,4780,5494,3875,3564,3276,1360,2040,57,2123,2403,2124,55,111,1744,501212,500672,500605,500292,7257,6938,6594,6490,6285,5866,5493,5297,4781,4736,4333,4197,4148,3387,3275,2606,2125,2402,78,382,105,92,241,1221,54,644,56,61,112,76,79,1055,1291,1795,2415],"data":{"id":500286,"name":"莱欧斯利","summary":"莱欧斯利","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/17/4328207/e1f49352e0f53081cf6d7758cc9a8eef_77085490975887896.png","avatar":{"m":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/e47523bb0dd8ee78d9f0d3e7dd8fe423_8339092036174132033.png","pc":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/bb3881139a1bfd61bd4ec61583e2adaf_6287286943608685046.png"},"attrs":[["地区","枫丹廷"],["星级","五星"],["神之眼所属","枫丹"],["元素","冰"],["武器","法器"],["始基力","荒"],["生日","11月23日"],["所属","梅洛彼得堡"],["定位","近战冰元素角色"],["武器类型","法器"],["命之座","守狱犬座"],["称号","寂罪的密使"]],"destiny":[["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/8727013762762392b93509c4df54675f_746841332408607555.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/8727013762762392b93509c4df54675f_746841332408607555.png\\" style=\\"width: 40px; border: \\"></span></span></span>予行恶者以惩惧</p>","<p>固有天赋「公理终有辩诉之日」的「恩典之诫」转变为：</p><p>莱欧斯利的生命值低于60%，或在冰牙突驰的寒烈的惩裁状态持续期间，斥逐拳的第五段攻击命中时，</p><p>将获得「恩典之诫」，每2.5秒至多获得一次「恩典之诫」效果。</p><p>此外，惩戒·凌跃拳获得以下强化：·造成的伤害提升进一步提升至200%；</p><p>·命中时，若处于寒烈的惩裁状态下，该状态的持续时间延长4秒，</p><p>在一次寒烈的惩裁状态持续期间，至多通过这种方式延长一次。</p><p>需要解锁固有天赋「公理终有辩诉之日」。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/f14397fc63f469afc48bcec2750a2b85_5141200759479347417.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/f14397fc63f469afc48bcec2750a2b85_5141200759479347417.png\\" style=\\"width: 40px; border: \\"></span></span></span>予骄暴者以镣锁</p>","<p>施放黑金狼噬时，固有天赋「罪业终有报偿之时」的每层「检偿之敕」</p><p>效果都将使造成的伤害提升40%。</p><p>需要解锁固有天赋「罪业终有报偿之时」。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/9ceca39961c06e769264cc43b9e683aa_695863396017822562.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/9ceca39961c06e769264cc43b9e683aa_695863396017822562.png\\" style=\\"width: 40px; border: \\"></span></span></span>予婪诈者以谴罚</p>","<p>普通攻击·迅烈倾霜拳的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/7939eb2083eda1c362be8d6e11172883_2141531695951406946.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/7939eb2083eda1c362be8d6e11172883_2141531695951406946.png\\" style=\\"width: 40px; border: \\"></span></span></span>予贞苦者以拯赎</p>","<p>惩戒·凌跃拳为莱欧斯利恢复生命值的回复量提升为50%生命值上限，需要解锁固有天赋</p><p>「公理终有辩诉之日」。</p><p>此外，莱欧斯利受到治疗时，若此次治疗回复量溢出，将根据莱欧斯利是否处于当前场上产生不同的效果：</p><p>处于当前场上时，莱欧斯利的攻击速度提升20%，持续4秒；</p><p>处于队伍后台时，队伍中所有自己的角色攻击速度提升10%，持续6秒。</p><p>上述这两种攻击速度提升效果无法叠加。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/14b68aa3dc43ca13a3ed83662bff4276_730316469511138086.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/14b68aa3dc43ca13a3ed83662bff4276_730316469511138086.png\\" style=\\"width: 40px; border: \\"></span></span></span>予冤诉者以怜恕</p>","<p>黑金狼噬的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/82379e0766e9776091c1d2f563f04b4e_3189642627542332535.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/08/372312395/82379e0766e9776091c1d2f563f04b4e_3189642627542332535.png\\" style=\\"width: 40px; border: \\"></span></span></span>予无罪者以念抚</p>","<p>惩戒·凌跃拳的暴击率提升10%，暴击伤害提升80%，</p><p>并能够额外生成冰锥，造成原本100%的冰元素伤害，</p><p>通过这种方式造成的伤害视为重击伤害。</p><p>需要解锁固有天赋「公理终有辩诉之日」。</p>"]],"skill":[{"title":"普通攻击·迅烈倾霜拳","desc":"普通攻击\\n将坚冰凝聚于拳锋，挥出迅猛的斥逐拳，进行至多五段的快速打击，造成冰元素伤害。\\n此外，施放元素战技「冰牙突驰」或进行冲刺后的一小段时间内，普通攻击连击段数不会重置。\\n\\n重击\\n消耗一定体力，腾跃而起，打出凌跃拳，造成冰元素范围伤害。\\n\\n下落攻击\\n从空中下坠冲击地面，攻击下落路径上的敌人，并在落地时造成冰元素范围伤害。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/d7b8f5f2fe1f5b984425a2e1d906df41_2739578026307034740.png"},{"title":"冰牙突驰","desc":"迅速调整呼吸、节奏与步伐，向前短距离冲刺，进入寒烈的惩裁状态，以发起更为猛烈的进攻。\\n\\n寒烈的惩裁\\n·提高莱欧斯利的抗打断能力；\\n·生命值高于50%时，强化「普通攻击·迅烈倾霜拳」的斥逐拳，使其造成的伤害提升；命中时莱欧斯利将损失一定的生命值，每0.1秒至多因此损失一次生命值；\\n效果将在莱欧斯利退场时解除。\\n\\n「过去的裁罚已成定局，未来的选择犹在掌握。」\\n「若是不愿命运被一时的罪恶吞尽，还请珍重此刻的光阴。」","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/e60fbe6aef5e5c216c5bc07d94ffa09f_3011765586546950506.png"},{"title":"黑金狼噬","desc":"\\n驱动拳套，打出凝聚寒霜的直拳，持续以冰锥冲击前方区域，造成多次冰元素范围伤害。\\n\\n始基力：荒性\\n冰锥冲击结束后，将在敌人的位置降下流涌之刃，造成具有荒性的冰元素伤害。\\n\\n总有一些事情，就算是梅洛彼得堡的公爵也会感到无可奈何——例如繁冗的文书工作，琐细的监管职责，或是…小小的友人们在自己拳套上偷偷贴一堆难撕的可爱贴纸的执着。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/6fbf99569c7ff219fa69bcd3340f6bbd_2774394899414257700.png"},{"title":"公理终有辩诉之日","desc":"莱欧斯利的生命值低于60%时，将获得「恩典之诫」，使下次普通攻击·迅烈倾霜拳的重击强化为惩戒·凌跃拳：不消耗体力，造成的伤害提升50%，并在命中后为莱欧斯利恢复生命值，回复量相当于莱欧斯利生命值上限的30%。\\n每5秒至多通过这种方式获得一次「恩典之诫」效果。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/34961d114c36564b668f8bcaa5a63eaf_441421074397000459.png"},{"title":"罪业终有报偿之时","desc":"莱欧斯利的当前生命值提升或降低时，若莱欧斯利处于冰牙突驰的寒烈的惩裁状态下，寒烈的惩裁将获得一层「检偿之敕」效果，该效果至多叠加至5层，每层使莱欧斯利的攻击力提升6%。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/af9681afe383c5b5393cf8c60563aa1b_7154142708572544519.png"},{"title":"公爵的恩馈","desc":"合成武器突破素材时，有10%概率获得2倍产出。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2023/10/10/4328207/fd752af9261ad54b850d73da132f0cbe_9083250277751341607.png"}]},"favModalLocalKey":"vitepress_game_ys_role_fav_modal","favLocalKey":"vitepress_game_ys_role_fav"},"relativePath":"game/ys/role/500286.md","filePath":"game/ys/role/[pkg].md"}'),i={name:"game/ys/role/500286.md"},g=Object.assign(i,{setup(o){return(c,d)=>(e(),p("div",null,[s(t(a))]))}});export{r as __pageData,g as default};
