import{_ as a}from"./chunks/index.VAya1KE4.js";import{c as p,o as s,G as t,k as e}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const r=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":501625,"idList":[504621,504570,504440,501214,503614,501157,501213,6489,3386,1058,1433,1056,1220,1498,503613,503612,4074,4073,500207,1290,502928,502927,5111,1627,502306,500987,501624,2404,501625,501626,2142,4334,6180,59,75,1057,501441,4081,500419,5020,500291,5865,500286,6937,500673,4780,5494,3875,3564,3276,1360,2040,57,2123,2403,2124,55,111,1744,501212,500672,500605,500292,7257,6938,6594,6490,6285,5866,5493,5297,4781,4736,4333,4197,4148,3387,3275,2606,2125,2402,78,382,105,92,241,1221,54,644,56,61,112,76,79,1055,1291,1795,2415],"data":{"id":501625,"name":"玛拉妮","summary":"哗啦啦逐浪客 · 玛拉妮","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/b2a2cf3412e9399a0a519525b330689e_2457703043196193750.png","avatar":{"m":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/986684113e9fdb1890ab8c1c45689871_7837129306274514402.png","pc":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/434858038e9434c946f4c66ce52fff94_8001399395561747256.png"},"attrs":[["地区","纳塔"],["星级","五星"],["神之眼所属","纳塔"],["元素","水"],["武器","法器"],["生日","8月3日"],["所属","梅兹特利"],["定位","水元素输出型角色"],["武器类型","法器"],["命之座","浪海豹座"],["称号","哗啦啦逐浪客"]],"destiny":[["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/194a010608d4ee47a27149bfa1263364_7259398861092026075.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/194a010608d4ee47a27149bfa1263364_7259398861092026075.png\\" style=\\"width: 40px;\\"></span></span></span>悠闲的「梅兹特利」…</p>","<p>进入夜魂加持状态后的第一次巨浪鲨鲨撕咬及它所触发的鲨鲨飞弹造成的伤害提升，提升值相当于玛拉妮生命值上限的66%</p><p>此次伤害提升遵循<strong><span style=\\"color: rgb(212, 177, 6)\\">踏鲨破浪</span></strong>的伤害衰减规则。</p><p>此外，不处于战斗状态下时，玛拉妮的夜魂加持状态消耗的夜魂值与燃素降低30%。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/a3e8285235ad3c8480974e7869697d0d_6112177482414069085.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/a3e8285235ad3c8480974e7869697d0d_6112177482414069085.png\\" style=\\"width: 40px;\\"></span></span></span>全力以赴玛拉妮！</p>","<p>进入夜魂加持状态时，玛拉妮获得2层<strong><span style=\\"color: rgb(212, 177, 6)\\">踏鲨破浪</span></strong>的浪势充能;玛拉妮获得豚豚球时，获得1层浪势充能。</p><p>此外，玛拉妮在一次夜魂加持期间获得2个豚豚球的2秒内，额外恢复12点夜魂值。该效果需要解锁固有天赋「耐热型淡水豚豚」。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/fb869202d90f94dfbc5f40d67fc7caf8_3627805725196688443.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/fb869202d90f94dfbc5f40d67fc7caf8_3627805725196688443.png\\" style=\\"width: 40px;\\"></span></span></span>在欢乐海上冲浪</p>","<p><strong><span style=\\"color: rgb(212, 177, 6)\\">踏鲨破浪</span></strong>的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/8ce65a0faf971baaf8d595a65b106c3e_442445702715840103.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/8ce65a0faf971baaf8d595a65b106c3e_442445702715840103.png\\" style=\\"width: 40px;\\"></span></span></span>鲨鲨主食是豚豚。</p>","<p>获得豚豚球时，玛拉妮恢复8点元素能量；</p><p>该效果需要解锁固有天赋「耐热型淡水豚豚」。</p><p>此外，<strong><span style=\\"color: rgb(212, 177, 6)\\">爆瀑飞弹</span></strong>造成的伤害提升75%。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/3a70dbf4e8bc7027d951a3b5ec6e16df_6412712026105375010.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/3a70dbf4e8bc7027d951a3b5ec6e16df_6412712026105375010.png\\" style=\\"width: 40px;\\"></span></span></span>同款冲浪板有售！</p>","<p>爆瀑飞弹的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/f49cb32e2faaa2a449698a3c62fa10ca_4769237053105657980.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/f49cb32e2faaa2a449698a3c62fa10ca_4769237053105657980.png\\" style=\\"width: 40px;\\"></span></span></span>「流泉之众」的精神</p>","<p>对命之座「悠闲的『梅兹特利』…」的伤害提升效果，移除原本每次夜魂加持只能触发一次的限制。</p>"]],"skill":[{"title":"普通攻击·降温处理","desc":"普通攻击\\n进行至多三段攻击，造成水元素伤害。\\n\\n重击\\n消耗一定体力，向空中击出坠落时会造成水元素范围伤害的水豚球。\\n\\n下落攻击\\n凝聚水元素的力量，从空中下坠冲击地面，攻击下落路径上的敌人，并在落地时造成水元素范围伤害。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/8da2c38767a2492c297682ff2133833a_5786927479930969445.png"},{"title":"踏鲨破浪","desc":"以驾驭速度与水潮的心得，随时随地进入冲浪的架势。\\n\\n施放后，玛拉妮将获得60点夜魂值并进入夜魂加持状态。\\n\\n夜魂加持·玛拉妮\\n持续消耗夜魂值。夜魂值耗尽时，或是再次施放时，玛拉妮的夜魂加持将会结束。\\n夜魂加持状态具有如下特性:\\n\\n·搭乘鲨鲨冲浪板，提升玛拉妮的移动速度与抗打断能力。\\n玛拉妮在这种状态下能在水面与液态燃素上移动，并免疫液态燃素的伤害;\\n\\n·进行普通攻击时，将转为进行鲨鲨撕咬，基于玛拉妮的生命值上限造成具有夜魂性质的水元素伤害。\\n鲨鲨撕咬能够在空中进行，造成的伤害被视为普通攻击伤害；\\n\\n·在这种状态下接触敌人时，玛拉妮对敌人施加猎物标记，并获得1层浪势充能，浪势充能至多叠加3层，每0.7秒至多通过同一个敌人获得1层。\\n\\n浪势充能与猎物标记\\n进行鲨鲨撕咬时，玛拉妮将依据浪势充能的层数，提高造成的伤害，拥有3层浪势充能时，玛拉妮的鲨鲨撕咬将转为巨浪鲨鲨撕咬，造成的伤害进一步提升；并在此后清除浪势充能。\\n\\n鲨鲨撕咬命中敌人时，将移除其猎物标记，并向附近至多5名处于猎物标记标记状态下的敌人发射鲨鲨飞弹，造成相当于此次鲨鲨撕咬的伤害，并清除这些敌人的猎物标记；\\n\\n此次鲨鲨撕咬与鲨鲨飞弹的目标敌人超过1名时，造成的伤害将会降低，至多在达到3名敌人时降低28%。\\n\\n首先鲨鲨不只是一个冲浪板，它还是淡水鲨。其次，它也不是真的要吃，只是真的会咬。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/f0bff5b65a1dcef679608e4393a7d4f8_2860235272185662029.png"},{"title":"爆瀑飞弹","desc":"发射能追踪敌人的特大号鲨鲨导弹，基于玛拉妮的生命值上限造成具有夜魂性质的水元素范围伤害。\\n\\n玛拉妮之所以把鲨鲨导弹叫这个名字，是因为里面有水有火，听起来很温泉。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/eefa593c5c29c752616875e978070618_3371481755135512663.png"},{"title":"耐热型淡水豚豚","desc":"巨浪鲨鲨撕咬命中敌人时，将在附近产生一个豚豚球。\\n\\n获得豚豚球时，玛拉妮自己将恢复20点夜魂值。\\n\\n一次夜魂加持期间，至多通过这种方式产生2个豚豚球。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/a53de1208c412c3466e4da9990e31bf5_3975287419100859891.png"},{"title":"纳塔最好的向导","desc":"队伍中的附近的角色触发「夜魂迸发」后，玛拉妮将获得一层持续20秒、至多叠加3层的逐浪心得。\\n\\n施放爆瀑飞弹时，玛拉妮将清除所有逐浪心得，并基于清除的层数，基于生命值上限的15%/30%/45%提高本次爆瀑飞弹造成的伤害","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/27ad4ef6404ec8d2b13c117546c358aa_6414032941051419959.png"},{"title":"夜域赐礼·波涛顶底","desc":"在夜魂值耗竭后，玛拉妮将会转而消耗燃素来维持夜魂加持。\\n\\n在纳塔存在燃素机制的区域时，能够进行夜魂传递·玛拉妮。\\n\\n当前场上角色处于冲刺、游泳或是特定天赋赋予的移动状态下，或是位于一定高度的空中时，切换玛拉妮登场时触发:玛拉妮将进入夜魂加持状态，并获得40点夜魂值。自己的队伍每10秒能触发一次夜魂传递。\\n\\n此外，玛拉妮在夜魂加持状态下在水面与液态燃素上移动时：在纳塔地区时消耗的夜魂值或燃素降低75%，在纳塔地区外消耗的夜魂值降低35%","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/8b5ad4e5b4896771b0b30e3a81e38fa8_5266033714877771958.png"},{"title":"要诀是保持笑容!","desc":"在纳塔存在燃素机制的区域时，与部分采集物交互能够恢复15点燃素。\\n\\n此外，在小地图上显示周围的纳塔区域特产的位置。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/23/4328207/7363fef9d91cb9e49f8b4f16f82538b7_6084655578752554425.png"}]},"favModalLocalKey":"vitepress_game_ys_role_fav_modal","favLocalKey":"vitepress_game_ys_role_fav"},"relativePath":"game/ys/role/501625.md","filePath":"game/ys/role/[pkg].md"}'),o={name:"game/ys/role/501625.md"},g=Object.assign(o,{setup(i){return(c,n)=>(s(),p("div",null,[t(e(a))]))}});export{r as __pageData,g as default};
