import{_ as a}from"./chunks/index.VAya1KE4.js";import{c as p,o as s,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const r=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"艾梅莉埃","idList":[504621,504570,504440,501214,503614,501157,501213,6489,3386,1058,1433,1056,1220,1498,503613,503612,4074,4073,500207,1290,502928,502927,5111,1627,502306,500987,501624,2404,501625,501626,2142,4334,6180,59,75,1057,501441,4081,500419,5020,500291,5865,500286,6937,500673,4780,5494,3875,3564,3276,1360,2040,57,2123,2403,2124,55,111,1744,501212,500672,500605,500292,7257,6938,6594,6490,6285,5866,5493,5297,4781,4736,4333,4197,4148,3387,3275,2606,2125,2402,78,382,105,92,241,1221,54,644,56,61,112,76,79,1055,1291,1795,2415],"data":{"id":501441,"name":"艾梅莉埃","summary":"千缕之踪 · 艾梅莉埃","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/08/28/195563531/3753712ae6c479c8f1ee841f60b1835f_7919993437788900063.png","avatar":{"m":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/d44d496dff4f7481e7792aa5c630169b_4544159256057566154.png","pc":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/6785ba21c0eabf414be5fff3d5ee321a_3812840095914677720.png"},"attrs":[["地区","枫丹廷"],["星级","五星"],["神之眼所属","枫丹"],["元素","草"],["武器","长柄武器"],["始基力","芒"],["生日","9月22日"],["所属","枫丹廷"],["定位","草元素输出型角色"],["武器类型","长柄武器"],["命之座","香氛瓶座"],["称号","千缕之踪"]],"destiny":[["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/13a6710249ca79256f20bf718d87bebc_4350717753293097651.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/13a6710249ca79256f20bf718d87bebc_4350717753293097651.png\\" style=\\"width: 40px;\\"></span></span></span>淡香浸析</p>","<p>撷萃调香与固有天赋「余薰」的清露香氛造成的伤害提升20%，后者需要解锁固有天赋「余薰」。</p><p>此外，队伍中附近的角色对敌人触发燃烧反应或对处于燃烧状态下的敌人造成草元素伤害时，会额外生成一枚香韵。</p><p>此效果每2.9秒至多触发一次。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/c6b25f6507fc67ba60754717c39bb2cc_4011495146171211820.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/c6b25f6507fc67ba60754717c39bb2cc_4011495146171211820.png\\" style=\\"width: 40px;\\"></span></span></span>湖光顶调</p>","<p>撷萃调香、香氛演绎或固有天赋「余薰」的清露香氛（需解锁该固有天赋）命中敌人时，该敌人的草元素抗性降低30%，持续10秒。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/dd3a122ce987827e75708320845f4a94_8634403563548741932.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/dd3a122ce987827e75708320845f4a94_8634403563548741932.png\\" style=\\"width: 40px;\\"></span></span></span>郁馥臻萃</p>","<p>撷萃调香的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/8480f24951645264a24dfd716eabaedf_1384962563383768716.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/8480f24951645264a24dfd716eabaedf_1384962563383768716.png\\" style=\\"width: 40px;\\"></span></span></span>柔灯心韵</p>","<p>香氛演绎的持续时间延长2秒，且每名敌人被香露选为目标的间隔降低0.3秒。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/e3efa166e5e25c8ae0cb5d0674f0261d_1396706074248579405.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/e3efa166e5e25c8ae0cb5d0674f0261d_1396706074248579405.png\\" style=\\"width: 40px;\\"></span></span></span>纯露凝芳</p>","<p>香氛演绎的技能等级提高3级。</p><p>至多提升至15级。</p>"],["<p><span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/c7550e5bd4de7ed425bfe41ad3a92cf1_1791699487426983800.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px;\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/c7550e5bd4de7ed425bfe41ad3a92cf1_1791699487426983800.png\\" style=\\"width: 40px;\\"></span></span></span>茉洁香迹</p>","<p>施放撷萃调香或香氛演绎时，艾梅莉埃将获得「香迹留驻」，持续5秒。</p><p>持续期间，艾梅莉埃进行普通攻击或重击后，将会产生一枚香韵，且艾梅莉埃的普通攻击与重击将转为无法被附魔覆盖的草元素伤害，并提升造成的伤害，提升值相当于艾梅莉埃攻击力的300%。</p><p>「香迹留驻」效果将在通过这种方式产生4枚香韵或持续时间结束时解除。</p><p>每12秒至多获得一次「香迹留驻」效果。</p>"]],"skill":[{"title":"普通攻击·逐影枪术·改","desc":"普通攻击\\n进行至多四段的连续枪击。\\n\\n重击\\n消耗一定体力，进行向上的挑斩攻击。\\n\\n下落攻击\\n从空中下坠冲击地面，攻击下落路径上的敌人，并在落地时造成范围伤害。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/05a423e6838bd4073582a6d9ef3ad125_2109425625222692921.png"},{"title":"撷萃调香","desc":"创造柔灯之匣，造成草元素范围伤害。\\n\\n柔灯之匣\\n·间歇性对附近的敌人发射「纯露芳息」，造成草元素伤害；\\n·附近的敌人处于燃烧状态下时，将间歇性产生「香韵」，每2秒至多通过这种方式产生一枚香韵。\\n柔灯之匣会萃集附近的香韵，萃集两枚香韵后，其品阶将会提升，发射纯露芳息时，将额外发射一枚纯露芳息，且提高上述攻击的攻击范围与造成的伤害。\\n·同时至多存在一个艾梅莉埃自己创造的柔灯之匣。\\n柔灯之匣创造时的初始品阶为一阶，至多提升至二阶。\\n处于场上时，若8秒内未萃集香韵，则柔灯之匣的品阶将会回到一阶。\\n\\n始基力：芒性\\n每隔一段时间，艾梅莉埃通过这种方式创造柔灯之匣后，将在前方降下穿透敌人的灵息之刺，造成具有芒性的草元素伤害。\\n\\n「世间之景，正如无数芬芳、色彩与歌声悠长的应和。」","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/afd3c56690fb73479218dfef9a4b24b9_5652830693259790470.png"},{"title":"香氛演绎","desc":"引导匣中萃集的香氛，将其化作纯粹的草元素之力，创造柔灯之匣·三阶，并收存已有的柔灯之匣。\\n\\n存在期间内，柔灯之匣·三阶不会萃集附近的香韵，而是会持续降下香露，攻击范围内的敌人，造成草元素伤害。\\n持续期间，每0.3秒将会降下一枚香露，且每名敌人每0.7秒只能被选为一次目标。\\n持续时间结束时，将重新创造柔灯之匣·一阶；\\n若施放香氛演绎时收存了柔灯之匣，则改为放置已收存的柔灯之匣，并重置其持续时间。\\n香氛演绎持续期间，元素战技「撷萃调香」不会生成柔灯之匣。\\n\\n无论是什么样的香水，自接触空气的那一刻开始，其香调便会随着时间推移而改变，最终消溶在风与草木的低语中。\\n某种意义上，人们永远也无法欣赏同一瓶香水。\\n众生的轨迹也是这样。因此，或许可以说，香水便是人生的演绎。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/7b35e891b109aa41471d794fc96ce974_5415410228646988315.png"},{"title":"余薰","desc":"每萃集两枚香韵，柔灯之匣·二阶将消耗香韵，并浸析出「清露香氛」，对敌人造成相当于艾梅莉埃攻击力600%的草元素范围伤害。\\n该伤害不被视为元素战技伤害。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/be274df3648387b4ade2c64a399a7fcf_5213507551476224049.png"},{"title":"精馏","desc":"基于艾梅莉埃的攻击力，提升艾梅莉埃对处于燃烧状态下的敌人造成的伤害：\\n每1000点攻击力都将提升15%伤害，至多通过这种方式提升36%伤害。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/39b306ee478fe3078780f0322d4163a0_1741396425967741196.png"},{"title":"顶空集香","desc":"场上存在艾梅莉埃自己创造的柔灯之匣时，队伍中附近的所有角色对燃烧伤害的火元素抗性提升85%。","icon":"https://act-upload.mihoyo.com/wiki-user-upload/2024/07/30/4328207/f755600916691ea921b2b251318b85ee_5161710843252529966.png"}]},"favModalLocalKey":"vitepress_game_ys_role_fav_modal","favLocalKey":"vitepress_game_ys_role_fav"},"relativePath":"game/ys/role/艾梅莉埃.md","filePath":"game/ys/role/[pkg].md"}'),i={name:"game/ys/role/艾梅莉埃.md"},g=Object.assign(i,{setup(o){return(c,d)=>(s(),p("div",null,[e(t(a))]))}});export{r as __pageData,g as default};
