import{_ as a}from"./chunks/index.VAya1KE4.js";import{c as s,o as t,G as e,k as p}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const r=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"迪希雅","idList":[504621,504570,504440,501214,503614,501157,501213,6489,3386,1058,1433,1056,1220,1498,503613,503612,4074,4073,500207,1290,502928,502927,5111,1627,502306,500987,501624,2404,501625,501626,2142,4334,6180,59,75,1057,501441,4081,500419,5020,500291,5865,500286,6937,500673,4780,5494,3875,3564,3276,1360,2040,57,2123,2403,2124,55,111,1744,501212,500672,500605,500292,7257,6938,6594,6490,6285,5866,5493,5297,4781,4736,4333,4197,4148,3387,3275,2606,2125,2402,78,382,105,92,241,1221,54,644,56,61,112,76,79,1055,1291,1795,2415],"data":{"id":6180,"name":"迪希雅","summary":"迪希雅","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/03/21/195563531/3499be9a309249e2ddf1a0533af1f4c7_958475410876966785.png","avatar":{"m":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/184eb563f56f80fa94d54c427291e07b_3566966954491024009.png","pc":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/564f9e8f29d589dd8210001bcebf9f9c_3705406695886457151.png"},"attrs":[["地区","须弥城"],["星级","五星"],["神之眼所属","须弥"],["元素","火"],["武器","双手剑"],["生日","4月7日"],["所属","镀金旅团"],["定位","火元素辅助型角色"],["武器类型","双手剑"],["命之座","蝎尾鬃狮座"],["称号","炽鬃之狮"]],"destiny":[["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/00b61b83cbf641f96c1d31ed6344ddba_3987640926711783053.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/00b61b83cbf641f96c1d31ed6344ddba_3987640926711783053.png\\" style=\\"width: 40px; border: \\"></span></span></span>皎洁之火铓辉灿漫\\n</p>","<p>迪希雅的生命值上限提升20%；此外，基于生命值上限，迪希雅的以下攻击造成的伤害提高：熔铁流狱伤害提高值：生命值上限的3.6%；炎啸狮子咬伤害提高值：生命值上限的6%。</p>"],["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/01c3a58ddde4fd52c002e47d9e5c8515_8197901542105935815.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/01c3a58ddde4fd52c002e47d9e5c8515_8197901542105935815.png\\" style=\\"width: 40px; border: \\"></span></span></span>净沙利刃明映万乘\\n</p>","<p>施放熔铁流狱·剑域炽焰时，重新产生的净焰剑狱领域持续时间将延长6秒。此外，场上存在净焰剑狱领域时，领域内的当前场上角色受到攻击时，将使净焰剑狱下次协同攻击造成的伤害提升50%。</p>"],["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/c6d84233499053ca98abe925d13e3941_7581772315525398178.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/c6d84233499053ca98abe925d13e3941_7581772315525398178.png\\" style=\\"width: 40px; border: \\"></span></span></span>怒势疾迅如焚掠地\\n</p>","<p>炎啸狮子咬的技能等级提高3级。至多提升至15级。</p>"],["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/5758499fe8c9b20de6f1c57f4358e3fe_440067959527327176.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/5758499fe8c9b20de6f1c57f4358e3fe_440067959527327176.png\\" style=\\"width: 40px; border: \\"></span></span></span>服膺誓禁恪守无失\\n</p>","<p>炎啸狮子咬的炽鬃拳与焚落踢命中敌人时，将为迪希雅恢复1.5点元素能量，并基于生命值上限的2.5%，为迪希雅恢复生命值。该效果每0.2秒至多触发一次。</p>"],["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/8374734bb6051ccaec172c066109f954_2128504172851117593.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/8374734bb6051ccaec172c066109f954_2128504172851117593.png\\" style=\\"width: 40px; border: \\"></span></span></span>猎兽魁首卓荦不羁\\n</p>","<p>熔铁流狱的技能等级提高3级。至多提升至15级。</p>"],["<p>\\n<span class=\\"custom-image-view\\" data-image-url=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/413cd4128ae8eca2df284b811332f198_8907079132543220487.png\\" data-image-unit=\\"px\\" data-image-width=\\"40\\"><span class=\\"custom-image-wrapper\\" style=\\"width: 40px\\"><span style=\\"\\"><img class=\\"custom-image\\" src=\\"https://act-upload.mihoyo.com/wiki-user-upload/2024/01/10/372312395/413cd4128ae8eca2df284b811332f198_8907079132543220487.png\\" style=\\"width: 40px; border: \\"></span></span></span>燎燃利爪裂帛斫金\\n</p>","<p>炎啸狮子咬的暴击率提升10%。此外，在一次炽炎狮子的持续期间，炽鬃拳命中敌人并造成暴击后，将使持续期间内炎啸狮子咬的暴击伤害提升15%，炽炎狮子的持续时间延长0.5秒。该效果每0.2秒至多触发一次，至多通过这种方式使持续时间延长2秒，暴击伤害提升60%。</p>"]],"skill":[{"title":"普通攻击·拂金剑斗术","desc":"普通攻击\\n结合双手剑与体术，进行至多四段的连续攻击。\\n\\n重击\\n持续消耗体力，快速进行连续的斩击。\\n重击结束时，会进行一次格外有力的挥砍。\\n\\n下落攻击\\n从空中下坠冲击地面，攻击下落路径上的敌人，并在落地时造成范围伤害。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/bee2396ab7aa4ae384a895728e986c93_918065260548794796.png"},{"title":"熔铁流狱","desc":"迪希雅依据战局状况，施展不同的自创战斗技艺。\\n\\n净焰昂藏\\n当前场上不存在迪希雅自己的「净焰剑狱」时施展。\\n造成火元素范围伤害，并产生净焰剑狱领域。\\n\\n剑域炽焰\\n当前场上存在迪希雅自己的净焰剑狱时施展。\\n进行腾跃攻击，造成火元素范围伤害，并在新的位置重新产生净焰剑狱领域。\\n通过这种方式重新产生的净焰剑狱领域将继承原本的持续时间。\\n\\n在1次净焰剑狱领域的持续时间内，只能施展1次剑域炽焰。\\n\\n净焰剑狱\\n领域中的敌人受到伤害时，净焰剑狱将进行协同攻击，基于迪希雅的攻击力与生命值上限，对其造成火元素范围伤害。\\n 该效果每2.5秒至多触发一次。\\n提升领域中当前场上角色的抗打断能力，并在这些角色受到伤害时，消解部分伤害，将其转入「赤鬃之血」，由迪希雅在10秒内承担。\\n 赤鬃之血储存的伤害值达到或超过迪希雅生命值上限的一定比例时，将不再消解伤害。\\n\\n同时只能存在1个由迪希雅自己创造的净焰剑狱。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/fb7575312454383051348516ac20b356_5782009073841132727.png"},{"title":"炎啸狮子咬","desc":"释放炙热的怒火，放弃不便的大剑，进入「炽炎狮子」状态，提升抗打断能力。\\n在这种状态下，迪希雅将自动连续挥舞炽鬃拳，基于攻击力与生命值上限造成火元素伤害，并在持续时间结束时，进行焚落踢，基于攻击力与生命值上限造成火元素范围伤害。\\n施放时，如果场上存在迪希雅自己的元素战技「熔铁流狱」的「净焰剑狱」领域，迪希雅会将其收回，并在炽炎狮子状态结束时在新的位置重新产生，重新产生时将继承被收回时的持续时间。\\n在这种状态下，迪希雅无法施放元素战技，无法进行普通攻击、重击与下落攻击。 「普通攻击·拂金剑斗术」与元素战技「熔铁流狱」将被替换为「炎咆连打」。\\n\\n炎咆连打\\n施展炽鬃拳后的0.4秒内施放炎咆连打，将提升下一次炽鬃拳施展的速度。\\n\\n流变之火也要顺从她的怒意，变化利爪尖牙般的锋锐之型。\\n\\n同度过这赤焰之试，烧却不洁吧，就如炽光之兽必自焚灼中降临那样。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/26a39d8b55271ccd6f8c8a8a3ccbdfbe_1311508313922333746.png"},{"title":"不吝佑助","desc":"迪希雅通过熔铁流狱·剑域炽焰或炎啸狮子咬回收净焰剑狱后的6秒内，迪希雅承担来自赤鬃之血的伤害时，受到的伤害降低60%。 该效果每2秒至多触发一次。\\n此外，迪希雅施放熔铁流狱·净焰昂藏后的9秒内，将为队伍中所有角色赋予「熔金铸躯」状态。\\n 处于熔金铸躯状态下的角色处于净焰剑狱领域中时，将获得额外的抗打断能力。\\n 每18秒至多产生一次熔金铸躯状态。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/d3dbf1bc982edf18ad151c302855c185_8220429111607688801.png"},{"title":"崇诚之真","desc":"生命值低于40%时，迪希雅将基于生命值上限的20%恢复生命值，并在接下来的10秒内，每2秒基于生命值上限的6%为迪希雅恢复生命值。\\n 该效果每20秒至多触发一次。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/5e8025b9e691fa570dd4eecfe65c316f_6722155152174665866.png"},{"title":"日照的正途","desc":"在白天（6时至18时），队伍中自己的角色获得迅行效果：移动速度提高10%。\\n该效果在秘境、征讨领域、深境螺旋中无效，迅行效果无法叠加。","icon":"https://uploadstatic.mihoyo.com/ys-obc/2023/02/27/4328207/df319420ec450096a2cb7000a9e48e0d_5220484084539781404.png"}]},"favModalLocalKey":"vitepress_game_ys_role_fav_modal","favLocalKey":"vitepress_game_ys_role_fav"},"relativePath":"game/ys/role/迪希雅.md","filePath":"game/ys/role/[pkg].md"}'),c={name:"game/ys/role/迪希雅.md"},g=Object.assign(c,{setup(o){return(i,d)=>(t(),s("div",null,[e(p(a))]))}});export{r as __pageData,g as default};
