import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as o,o as a,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":1109,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":1109,"name":"","summary":"耀嘉音","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/01/21/76099754/d1193bcbdf67de30128e8a1e288816bf_1810427348683629817.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2025/01/15/15559334/53beea90d87c4a092fee6f0ce7b97662_6824012795652384624.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2025/01/17/76099754/713c2ed63f81cd9ecb2b1603a9f1be16_1584642328525327704.png"},"attrs":[["稀有度","S"],["属性","以太"],["特性","支援"],["阵营","天琴座"]],"destiny":[["LEVEL1十二平均律","耀嘉音攻击命中敌人时，目标的全属性伤害抗性降低6%，最多叠加3层，持续30秒，重复触发时刷新持续时间；进入战场时，耀嘉音立刻获得1000点喧响值；发动[终结技]时，全队各自获得1层[庇佑之音]效果，持续60秒，受到敌方攻击时，将消耗[庇佑之音]并获得无敌效果，持续1秒。「老师老师，什么是十二平均律？」「十二平均律是一种音乐的律式，就像小朋友们要排队队一样，声音也要排队队。」「那声音肯定也和小朋友一样，不爱排队队！」——嘉音六岁时的音乐老师，在她成名之后回忆道。"],["LEVEL2贪心艺术","[核心被动：《如歌的行板》]中的攻击力提升效果额外提升19%，上限额外提升400点；[咏叹华彩]状态下，队伍中其他角色通过[快速支援]、[连携技]、[招架支援]或[回避支援]入场时，耀嘉音将额外追加释放1道[震音]和3枚[音簇]进行攻击，3秒内最多触发一次。「这个世界上最闪亮的东西，当然是爱！」像筑巢的小鸟，想要收集亮晶晶的东西。艺术家内心的那份小贪心，就是收集世界上所有的爱吧？只要将所有的爱汇聚在一起，就能创作出最动人的旋律。"],["LEVEL3交错五线谱","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2空白的五线谱，是永不相交的平行线，就像未曾相遇的灵魂，静静等待着彼此的旋律交织成曲。「那就用音符填满它好了！还要很多很多连音符，会是一首很棒的歌吧？」就这样创作了一首从头到尾用连音符横跨了136小节的新歌…"],["LEVEL4《后颈的碎发》","发动[连携技]或[终结技]后，15秒内，耀嘉音未拥有[和弦]时也能够触发[快速支援]，且攻击命中敌人时触发[快速支援]的间隔缩短至1秒；[咏叹华彩]状态下，队伍中其他角色通过[快速支援]入场时，将根据自身特性获得不同的增益效果，全队3秒内最多触发一次；[强攻]：下一次[快速支援]重击命中敌人时，额外造成等同于耀嘉音300%攻击力的伤害；[异常]：下一次[快速支援]累积的属性异常积蓄值提升50%；[击破]：下一次[快速支援]造成的失衡值提升50%。一只手悬在钢琴上不知该按下哪一个键是悲伤的小调还是悠扬的思念？在音符间导觅你的影子闭上眼，就能看到你后颈的碎发"],["LEVEL5绳匠与丝弦","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「伊芙！我有一个重大发现——伊芙和铃/哲都是长条形的！」「…这两者的共性是什么？」「你们都是我最喜欢的人啊！」嘉音像发现了什么不得了的机密一般，开心而明媚。"],["LEVEL6我们即是世界","在[咏叹华彩]状态下释放和追加释放的[震音]和[音簇]的伤害倍率提升至原本的200%，暴击率提升80%；触发[精准支援]时，耀嘉音会自动追加发动蓄力完成后的[普通攻击：《随想曲》]第三段攻击，并使该招式的暴击率提升80%，10秒内最多触发一次。世界的纷纷扰扰，都是源自人心的孤寂。如果每个人的心都可以得到回音，会不会，这个世界的苦难、悲伤、沉默都可以被统统抹去?若能用歌声编织起这份连结，是否所有的裂痕都会愈合，所有的痛楚都会化作悠扬的音律，在寂静中响起？那时，整个世界，便不再是任何一座孤岛。「这首歌，献给这个世界！」"]],"skill":[{"title":"普通攻击：《随想曲》","desc":"点按发动：向前方进行至多三段的攻击，造成以太伤害；第三段攻击期间，可长按进行蓄力，持续聚集敌人，提升攻击范围，释放更加强力的攻击；其他招式发动后，可长按直接发动第三段攻击。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/86a16124e0b127922c32da5740ca8894_7702882446927108933.gif"},{"title":"普通攻击：间奏","desc":"[咏叹华彩]状态下，点按发动：向前方释放[震音]进行攻击，造成以太伤害；招式命中敌人时，将触发[快速支援]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/88635da8e14549dcd7548c21dc79257f_7125817310755288684.gif"},{"title":"普通攻击：副歌","desc":"[咏叹华彩]状态下，触发[极限闪避]后，点按发动：对前方敌人发动多道[震音]进行攻击，造成大量以太伤害；招式命中敌人时，将触发[快速支援]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/3751aa935566b4ff851074b39937055f_8867997791853529671.gif"},{"title":"普通攻击：终曲","desc":"[咏叹华彩]状态下，长按发动：向前方释放[震音]进行攻击，造成以太伤害，并退出[咏叹华彩]状态；招式发动时，会对自身施加[核心被动：《如歌的行板》]中的增益效果；能量足够时，会消耗能量追加释放1道[震音]和3枚[音簇]进行攻击，该次[震音]会被视为[强化特殊技]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/df9ba05f2661a17e29eebde9234c07e1_5943625376147977554.gif"},{"title":"普通攻击：《随想曲》（蓄力）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/9316929b9589a36211a9108bb4104a00_6205581301256314507.gif"},{"title":"普通攻击：终曲（能量追加）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/4ea88b9c81ad9729b2d5808c732189e1_6934403990774177759.gif"},{"title":"闪避：《小华尔兹》","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/20652f32f7712c35ec2a6a33711191b1_7130817330804395584.gif"},{"title":"冲刺攻击：《蚀月奏》","desc":"闪避时，点按发动；向前方进行攻击，造成以太伤害；发动时若拖曳摇杆，可以向对应方向位移。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/0a9e13f8c092f398194ab01af780a215_2269593649893706835.gif"},{"title":"闪避反击：《折伞华尔兹》","desc":"触发[极限闪避]后，点按发动：向前方敌人发动攻击，造成以太伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/7baac2372a6621fee9386a939dd02d4f_8817727355024610657.gif"},{"title":"快速支援：《一川烟火》","desc":"当前操作中的角色被击飞时，点按发动：向前方敌人发动攻击，造成以太伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/1ae00d2097e46355bdff44de42e3e78d_3827047467216033206.gif"},{"title":"回避支援：《两份相惜》","desc":"前场角色即将被攻击时，点按发动：回避敌人的攻击，并触发[极限视域]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/2dc8cd475508e6dd4e61aacb48ce0ed0_8521881966586270551.gif"},{"title":"支援突击：《三生初见》","desc":"发动[回避支援]后，点按发动：向前方发出能量光束，造成以太伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/d5fe6621b65fd2531d850593307c7552_4413083117975557219.gif"},{"title":"特殊技：《风铃与旧约》","desc":"点按发动：快速位移，随后释放[震音]造成以太伤害，并进入[咏叹华彩]状态：发动时若拖曳摇杆，可以向对应方向位移；发动时可长按进行蓄力，持续聚集敌人，并根据蓄力时长追加释放1~4道[震音]；招式命中敌人时，将触发[快速支援]；快速位移期间拥有无敌效果。咏叹华彩耀嘉音进入[咏叹华彩]状态后，全队造成的伤害提升9%，全队暴击伤害提升8.5%；（此处数据为初始数据）[咏叹华彩]状态下，耀嘉音无法移动，但可以点按发动[普通攻击：间奏]，或点按发动闪避，触发[极限闪避]后，点按，可以发动[普通攻击：副歌]；状态持续期间，耀嘉音的抗打断等级提升，受到的伤害降低40%，切换至其他角色后，耀嘉音会继续留在前场，并获得无敌效果，切换回耀嘉音时，则会自动衔接发动[特殊技：《风铃与旧约》]；状态持续期间，长按发动[普通攻击：终曲]，或长按，可以退出[咏叹华彩]状态。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/f41e901dd52a1353928c9bd7a7ccc401_8368613948318182199.gif"},{"title":"和弦","desc":"每拥有25点能量，耀嘉音将拥有1点[和弦];[咏叹华彩]状态下，耀嘉音拥有[和弦]时，能够在以下情况中触发[快速支援]：操作中的角色攻击命中敌人时，3秒内最多触发一次；操作中的角色重击命中敌人时，1秒内最多触发一次；队伍中其他角色通过[快速支援]、[连携技]、[招架支援]或[回避支援]入场时，耀嘉音将消耗25点能量，并追加释放1道[震音]进行攻击，该次[震音]会被视为[强化特殊技]；触发[快速支援]后，短时间内点按切换角色，将触发[精准支援]，耀嘉音会额外追加释放3枚[音簇]进行攻击，队伍中其他角色通过[连携技]、[招架支援]或[回避支援]入场时，将直接触发[精准支援]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/ce90c8a23964034e6b53561d9e2b1938_3785877398563043837.gif"},{"title":"特殊技：《风铃与旧约》（长按）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/e78e9db3fd476869927a74b5fc060d9f_4366671039731276752.gif"},{"title":"咏叹华彩（驻场特效）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/52e5d044b0ff706111b181f190ebff67_6928425164815585925.gif"},{"title":"连携技：《微醺协奏》","desc":"触发[连携技]时，选择对应角色发动：用歌声引导能量轰击，造成大量以太伤害；招式发动期间拥有无敌效果；招式命中敌人时，将触发[快速支援]；招式发动后，耀嘉音将进入[咏叹华彩]状态。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/b54cc7857caaf24b41da5b6d13deb45f_4464165476465732524.gif"},{"title":"终结技：《幻想式奏鸣》","desc":"喧响等级达到[极]时，点按发动：用歌声引导能量轰击，造成大量以太伤害，并为全队回复200点生命值；（此处数据为初始数据）招式发动期间拥有无敌效果；招式命中敌人时，将触发[快速支援]；招式发动后，耀嘉音将进入[咏叹华彩]状态；招式发动后，下两次发动的[快速支援]，将被替换为[连携技]。次数耗尽前，全队[连携技]或[终结技]，将触发[快速支援]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/6b91eb4c83eb117a88e22098cc413a16_5163583191385522706.gif"},{"title":"终结技：《幻想式奏鸣》（多次释放）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/1a331574a7e22271e888d154596e548c_5115148842648915778.gif"},{"title":"核心技","desc":"核心被动：《如歌的行板》[咏叹华彩]状态下，队伍中其他角色通过[快速支援]、[连携技]、[招架支援]或[回避支援]入场，耀嘉音消耗能量追加释放[震音]进行攻击时，耀嘉音和入场角色的攻击力提升，提升效果等同于耀嘉音初始攻击力的22%，最高不超过1200点，持续20秒，重复触发时延长持续时间，最多不超过30秒。（此处数据为初始数据）额外能力：《月华乱舞》队伍中存在[强攻]或[异常]角色时触发：消耗能量发动[精准支援]或[普通攻击：终曲]时，耀嘉音将额外追加释放1道[震音]和3枚[音簇]进行攻击。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/c4e7dc00f66c7f43c23cb85463d43d0b_3231726343164909287.gif"},{"title":"震音&音簇","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2025/02/02/82232455/e3a3d6678af5ba943df7f2102f0db9c2_3818607199474533149.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/1109.md","filePath":"game/zzz/role/[pkg].md"}'),d={name:"game/zzz/role/1109.md"},h=Object.assign(d,{setup(i){return(p,n)=>(a(),o("div",null,[e(t(c))]))}});export{l as __pageData,h as default};
