import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":150,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":150,"name":"格莉丝","summary":"格莉丝·霍华德","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/21/160824341/e16ac4c5e1f06ba44b1fd48539916be3_3243900714756554770.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/6f433bd45fb657c1dd79e6cbef0c0c29_7630233985482675374.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/d98501189911794e65a7bbcb205e65ed_6543737891696181009.png"},"attrs":[["稀有度","S"],["属性","电"],["特性","异常"],["阵营","白祇重工"]],"destiny":[["LEVEL1再充能弹膛","[普通攻击]第四段每次命中敌人时，全队回复0.25点能量；同一招式内，该效果最多为全队回复2点能量。效率是工程学的生命。在格莉丝的枪膛里，回响着工程学的心跳声。"],["LEVEL2电致击穿","在[普通攻击]、[特殊技]、[强化特殊技]等招式中投掷手雷命中敌人时，目标的电属性伤害抗性降低8.5%，电属性异常积蓄抗性降低8.5%，持续8秒。「解剖」复杂的机械结构是格莉丝最大的爱好，即使是在战斗之中。被解剖者会作何感想呢？当它们每一方寸的破绽都如此赤裸。"],["LEVEL3首席机械师","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2格莉丝亲自参与编写了《白祇重工安全施工指南》，并且成为了违反「禁止在施工场地携带危险爆炸物」条例次数最多的员工。"],["LEVEL4爆破电容","发动[强化特殊技]时，格莉丝获得6层充能（上限6层）；[普通攻击]或[冲刺攻击]命中敌人时，消耗1层充能，使当前招式的能量获得效率提升20%。安东怀疑，在被格莉丝称为「电脉冲回收模块」的元件里，其实有一只以太仓鼠在不分昼夜地鼠力发电。「能在我兄弟上也整一个吗？」"],["LEVEL5「冰冷铁魔女」","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「冰冷铁魔女」能够驯服任何机械造物，至少起这个名字的匿名粉丝是这么认为的。"],["LEVEL6起爆扳机","消耗所有[电能]发动[特殊技]或[强化特殊技]时，能够强化招式效果，额外投掷一颗手雷，并使每颗手雷造成的伤害提升至原本的200%。白祇重工旗下员工的平均血液温度是九十八摄氏度，格莉丝总是被当作沸腾前的降温阀。当格莉丝放弃了克制，大家就知道，是时候把耳朵捂上了。"]],"skill":[{"title":"普通攻击：高压射钉","desc":"点按发动：向前方进行至多四段的攻击，造成物理伤害和电属性伤害；在[普通攻击]期间向侧方拖曳摇杆，可以发动垫步射击，调整身位并造成物理伤害；格莉丝在[普通攻击]期间发动[特殊技]、[强化特殊技]、[闪避]、[冲刺攻击]、[闪避反击]时，不会打断[普通攻击]的连击段数。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/21/160824341/b39902d599f484dca9a6009ec2359eef_5685134127537112798.gif"},{"title":"闪避：安全条款","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/284550490/3a3ea574b9b4bbf2b5b831f14439636f_3517498588731867719.gif"},{"title":"冲刺攻击：突击检查","desc":"闪避时，点按发动：向前方滑铲扫射，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/a5534dd37322f77a32bd484649a33bed_1180429515620756035.gif"},{"title":"闪避反击：违章处罚","desc":"触发[极限闪避]后，点按发动：对敌人投掷一颗手雷，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/b68bea49cdddcdb1d73a560befd1599d_9088403917935174818.gif"},{"title":"快速支援：事故解决方案","desc":"当前操作中的角色被击飞时，点按发动：对敌人投掷一颗手雷，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/69bd65b3e12ab080af20eb190ff9dc32_7933205157342439459.gif"},{"title":"回避支援：快速风险响应","desc":"前场角色即将被攻击时，点按发动：回避敌人的攻击，并触发[极限视域]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/8ab1c6f9f8dc34dff6a41d9c2c246d5f_1202404413962500643.gif"},{"title":"支援突击：反击电针","desc":"发动[回避支援]后，点按发动：对前方敌人发动回旋扫射并投掷手雷，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/36db4249cde1ff363b4eec8396fc0314_5726051921342935157.gif"},{"title":"特殊技：工程清障","desc":"点按发动：向前方投掷一颗手雷，造成电属性伤害；发动招式时，可以拖曳摇杆，向对应方向调整身位；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/ce7b61370d5eca6a62c82892020d73fe_5522636251591189861.gif"},{"title":"强化特殊技：超规工程清障","desc":"能量足够时，点按发动：腾空跃起，并向前方投掷两颗手雷，造成大量电属性伤害；发动招式时，可以拖曳摇杆，向对应方向调整身位；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/8252fa526c14768ff3b19dd0a987f39d_4679885462239259995.gif"},{"title":"连携技：协作施工","desc":"触发[连携技]时，选择对应角色发动：向空中抛出三颗手雷，随后扫射将其引爆，造成大量电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/8eebfa74dbeb786585f5499a428f7859_6726506168538977931.gif"},{"title":"终结技：工程爆破请勿接近","desc":"喧响等级达到[极]时，点按发动：丢出一枚特制的手雷，其在空中爆炸后会分裂成四颗更小的手雷，造成大量电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/284550490/f8ab60940f5cac4066d60c9959b644c4_4383500706536680693.gif"},{"title":"核心技","desc":"核心被动：预驱动撞针格莉丝发动[普通攻击]或[冲刺攻击]命中敌人并造成物理伤害时，获得一层[电能](上限8层)；[电能]层数到达上限后，发动[特殊技]或[强化特殊技]时，消耗所有[电能]，使招式聚积的电属性异常积蓄值提升65%。（此处数据为初始数据）额外能力：技术支持班组队伍中存在与自身属性或阵营相同的角色时触发：格莉丝发动[强化特殊技]命中敌人时，目标下次被施加[感电]效果时，该次[感电]伤害提升18%，最多叠加2层，同一招式内每个敌人最多触发一次效果，[感电]状态结束时效果重置。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/03/222203892/67d0ab855cd32352576d1ce4a5e5cb06_5000515579135332764.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/150.md","filePath":"game/zzz/role/[pkg].md"}'),d={name:"game/zzz/role/150.md"},_=Object.assign(d,{setup(i){return(p,n)=>(o(),c("div",null,[e(t(a))]))}});export{l as __pageData,_ as default};
