import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":158,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":158,"name":"本","summary":"本·比格","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/25/76099754/63ea02398b416017922f38becab2cb20_5905737788415760146.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/26277d61bbfa99b6ebb6a492f370c680_7550120081324746593.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/5fb693a21be18e7df9732df1c916520e_8752413880560381296.png"},"attrs":[["稀有度","A"],["属性","火"],["特性","防护"],["阵营","白祇重工"]],"destiny":[["LEVEL1反作用力","发动[特殊技]或[强化特殊技]，成功格挡敌人的攻击时，攻击者造成的伤害降低30%，持续15秒。「力的作用是相互的，为什么还要正面撞过来? 」「看来你没有好好计算过」"],["LEVEL2以守为攻","在[特殊技]或[强化特殊技]中，成功触发格挡反击时，额外造成本300%防御力的伤害。不像其他熊希人，战斗时的本非常有耐心。因为他相信，只要做好防护，一定能等到敌人露出破绽的时刻。"],["LEVEL3精打细算","[普通攻击]、[闪避]、[特殊技]、[连携技]技能等级+2担当财务负责人必须学会精打细算，用一切可用的资源强化大家和自己。"],["LEVEL4灵活应变","在[特殊技]或[强化特殊技]中，通过无敌效果成功格挡攻击时，后继反击造成的伤害提升30%。出于会计的职业本能，本总是会根据形式，动态调整自己的对策。无论是账目还是对敌，务必以最小成本达到最佳效果。"],["LEVEL5巨细靡遗","[普通攻击]、[闪避]、[特殊技]、[连携技]技能等级+2本敏锐的眼光不会放弃任何报表上的错误，同样也不会放过自己战斗方式上的失误，在纠错中逐渐变强。"],["LEVEL6野性斗志","在[强化特殊技]中发动攻击或追击后，[普通攻击]、[冲刺攻击]、[闪避反击]造成的失衡值提升20%，持续12秒。熊希人与犬希人不同，并不能用食物安抚他们躁动的情绪。当他们发脾气时，你唯一能做的就是跑快一点，跑远一点...——下架书籍《做个好邻居！与希人相处的100个小窍门》"]],"skill":[{"title":"普通攻击：对账","desc":"点按发动：向前方进行至多三段的打击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/19/141353238/8459a6d0c5fe42679cf29e6aa8c6dc14_1409581255909890441.gif"},{"title":"闪避：发票漏洞","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/0272696f16c1804e81ead2cd6d1f6de1_3482976361379065771.gif"},{"title":"冲刺攻击：前来报销","desc":"闪避时，点按发动：向前方进行打击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/a36178cb61cfe7801f51baf609a33118_2366404784103892467.gif"},{"title":"闪避反击：清算","desc":"触发[极限闪避]后，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/0801aaae1e970a78d70e873c788b65f7_8785146216835137573.gif"},{"title":"快速支援：联合追债","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/2c1eb717f5c95007bce3fda8573ac290_9001167666651773726.gif"},{"title":"招架支援：分摊风险","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；具有较为优秀的招架性能，承受高强度攻击时[支援点数]的消耗量降低；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/caf4aa899356d4049782a48a138e25d1_5903638461678216076.gif"},{"title":"支援突击：违约惩罚","desc":"发动[招架支援]后，点按发动：对前方敌人发动突进打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/090404bd440eae5ae0de5c738b42596f_1184667081239109246.gif"},{"title":"特殊技：拳债统计","desc":"点按发动：架起武器格挡敌人的攻击，随后对前方进行打击，造成物理伤害，若在格挡期间成功格挡敌人的攻击，将提升招式的威力，发动格挡反击，造成大量物理伤害；按住能够维持格挡；维持格挡时获得护盾，护盾值随时间衰减；退出格挡后移除护盾，护盾值随时间回复；护盾值最高不超过本生命值上限的16%；格挡发动时拥有无敌效果；打击发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/08b854cb507e0ac6f6b7357db1447491_4357995577959791907.gif"},{"title":"强化特殊技：到期还拳","desc":"能量足够时，点按发动：架起武器格挡敌人的攻击，随后对前方进行打击，造成火属性伤害，若在格挡期间成功格挡敌人的攻击，将提升招式的威力，发动格挡反击，造成大量火属性伤害；按住能够维持格挡；维持格挡时获得护盾，护盾值随时间衰减；退出格挡后移除护盾，护盾值随时间回复；护盾值最高不超过本生命值上限的16%；招式发动时，回复护盾至最大值；招式发动后，再次点按，能够向前方追加强力打击，造成大量火属性伤害；格挡发动时拥有无敌效果；打击发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/7b38c48a9562c1ccf35820891578962d_8599982674377030365.gif"},{"title":"连携技：盖章，结算","desc":"触发[连携技]时，选择对应角色发动：对前方大范围敌人发动强力打击，造成大量火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/9f288c7c0c870a1182681c029d3d7e87_5084228424392894275.gif"},{"title":"终结技：拳债，全面清偿","desc":"喧响等级达到[极]时，点按发动：对前方大范围敌人发动强力打击，造成大量火属性伤害；招式发动时，额外回复3点支援点数；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/222203892/95541cdfa9d1dd6e789b48d81af9af4c_1068296101112769815.gif"},{"title":"核心技","desc":"核心被动: 守卫本的初始攻击力随初始防御力提升，提升效果等同于自身初始防御力的40%；在[强化特殊技]中发动追击时，为全队提供等同于自身15%防御力+100点护盾，持续30秒。（此处数据为初始数据）额外能力：协议合同队伍中存在与自身属性或阵营相同的角色时触发；拥有[核心被动: 守卫]施加的护盾时，角色的暴击率提升16%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/03/34600917/27a48182ab125affc9589d9de2129339_4498766982104206355.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/158.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/158.md"},_=Object.assign(i,{setup(d){return(p,n)=>(o(),c("div",null,[e(t(a))]))}});export{b as __pageData,_ as default};
