import{_ as e}from"./chunks/index.VAya1KE4.js";import{c as a,o as c,G as o,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":227,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":227,"name":"苍角","summary":"苍角","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/27/339584194/5dbe373f3e54d4e3033e4dc7567fe9f8_2360629584131406565.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/6980488721a3f699e5d6832641d7e0a2_4126018835610783344.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/9ddbda1f6219d0c1e1c4b376c8721174_3167063953923566677.png"},"attrs":[["稀有度","A"],["属性","冰"],["特性","支援"],["阵营","H.S.O.S.6"]],"destiny":[["LEVEL 1振奋汇流","苍角触发[核心被动：刃旗助威]或[额外能力：团膳套餐]时，增益效果的持续时间提升8秒。蓄能完毕后，特制充能模块可以形成以太能量风场，为周围的以太设备充能。对苍角来说，这就像她最喜欢的聚餐时刻。「大家~来吃自助餐咯！」"],["LEVEL 2实验性充能模块","[普通攻击]、[冲刺攻击]、[闪避反击]、[快速支援]命中敌人时，苍角有15%的概率获得一层[涡流]，1秒内最多触发一次；[涡流]层数到达上限后，若再次获得[涡流]。将改为苍角回复1.2点能量。充能模块能够在战斗中不断积蓄能量，因此被研发部门视作划时代的新概念战术兵器。但对苍角来说，蓄能完毕，只意味着开饭的时间到了。"],["LEVEL 3出勤餐补","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2要激励苍角努力锻炼的方法非常简单：提高出勤的餐饮补助标准就行。"],["LEVEL 4失温症","[展旗]攻击命中敌人时，目标的冰属性伤害抗性降低10%，持续8秒。特制武装战鬼刃旗内置强大的制冷装置，产生冷气的催化剂中含有以太物质，能深度伤害被击中的敌人。"],["LEVEL 5夜宵福利","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2要进一步激励苍角努力锻炼的方法也很简单：在出勤餐补之外再追加夜宵。"],["LEVEL 6朔风侵袭","[霜染刃旗]状态下，强化[普通攻击]和强化[冲刺攻击]的可发动次数提升至12次，且招式造成的伤害提升45%。解开刃旗内的保险装置，让武器以最大功率模式运转，不仅战斗没问题...用来吹凉便当也没问题。"]],"skill":[{"title":"普通攻击：打年糕","desc":"点按发动：向前方进行至多三段攻击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/06/82232455/59e9640e778457d853d98ececf756a37_3906206885289287291.gif"},{"title":"普通攻击：打年糕（霜染刃旗）","desc":"[霜染刃旗]状态下，点按发动：向前方进行至多三段更快速的攻击，造成冰属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/29fe97e8c1e6e4ec5da86e91acc0f0d5_4255456217700450248.gif"},{"title":"闪避：抢食","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/73eb9d1888b56b7433312e28f931e10d_3556548940399813191.gif"},{"title":"冲刺攻击：对半分","desc":"闪避时，点按发动：向前方进行斩击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/2e15694da20c472fb2feadb6aef8eb7c_8858922815634533930.gif"},{"title":"冲刺攻击：对半分（霜染刃旗）","desc":"[霜染刃旗]状态下，闪避时，点按发动：向前方进行斩击，造成冰属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/654affdfbd3e5641ca8ac151b9f4aea6_2675713207290594541.gif"},{"title":"闪避反击：别抢零食","desc":"触发[极限闪避]后，点按发动：对前方敌人发动斩击，造成冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/c78e492604efd8398eeb4937570e035d_308705777370316156.gif"},{"title":"快速支援：双人套餐","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动斩击，造成冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/b8944e4675406b820d1734f9af505e5b_8448184971777116539.gif"},{"title":"招架支援：防守战术","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；具有较为优秀的招架性能，承受高强度攻击时[支援点数]的消耗量降低；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/4e46edf3c69aa51d070e948b86a2028f_6941718723861380329.gif"},{"title":"支援突击：席卷打击","desc":"发动[招架支援]后，点按发动：对前方敌人发动突进打击，造成冰属性伤害；招式发动期间拥有无敌效果。招式发动期间长按，可以快速衔接[展旗]攻击。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/3a96b67f4e101c28263d06e948a5261a_5776892783871126040.gif"},{"title":"特殊技：吹凉便当","desc":"点按发动：向前方扇出风场，随后释放终结一击，造成冰属性伤害；招式发动期间抗打断等级提升，受到的伤害降低40%；招式发动期间长按，可以快速衔接[展旗]攻击。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/3fb32532a655567a27284dc24e79282e_2171913517562619698.gif"},{"title":"强化特殊技：扇走蚊虫","desc":"能量足够时，点按发动：向前方扇出范围更远的风场，造成大量冰属性伤害；多次点按可以连续发动招式，每次发动时将消耗一定能量，能量不足或停止点按时会自动释放终结一击，造成大量冰属性伤害；招式发动期间抗打断等级提升，受到的伤害降低40%；招式发动期间长按，可以快速衔接[展旗]攻击。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/2767dbe5285980d1528a01adc5ab75bd_2326555545064537886.gif"},{"title":"特殊技：集合啦！","desc":"长按发动：将武器插入地面变形为旗帜，发动[展旗]攻击，造成冰属性伤害，[展旗]攻击命中敌人时，将触发[快速支援]；[展旗]攻击后再次点按或，将发动一次特殊的收旗攻击，造成冰属性伤害；发动[展旗]攻击时，若拥有三层[涡流]，则会消耗所有[涡流]，进入[霜染刃旗]状态；[霜染刃旗]状态下，苍角能够发动强化[普通攻击]和强化[冲刺攻击]，造成大量冰属性伤害，最多持续45秒或触发6次；苍角的武器在以下情况下会获得[涡流]：发动[强化特殊技]时，获得一层[涡流]；发动[连携技]时，获得一层[涡流]；发动[终结技]时，获得三层[涡流]；[涡流]上限为三层。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/6c15dc3c12e0f377730c53d8ad367b47_9156732102696552890.gif"},{"title":"连携技：鹅鸡斩","desc":"触发[连携技]时，选择对应角色发动：快速向前方敌人发动多次打击，随后衔接[展旗]攻击，造成大量冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/b8e68f1faf09c5e8f84e0916098ec0e1_6425506488266069578.gif"},{"title":"终结技：大份鹅鸡斩","desc":"喧响等级达到[极]时，点按发动：快速对前方敌人发动更多次打击，随后衔接[展旗]攻击，造成大量冰属性伤害；招式发动时，苍角会进入[鬼面]状态，使自身的暴击率提升15%，持续15秒；招式发动时，队伍中其他角色回复10点能量，下一名换入前场的角色额外回复20点能量；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/222203892/bece7bd483f122c7f2aa2cabfbbe1e10_8312480201600587287.gif"},{"title":"","desc":"核心被动：刃旗助威苍角发动[展旗]时，自身攻击力提升，提升数值等同于苍角10%初始攻击力，最高不超过500点，持续22秒，发动[展旗]并消耗[涡流]时，该增益效果翻倍，最高不超过1,000点。队伍中其他角色通过[展旗]所触发的[快速支援]或[连携技]入场时，该增益效果将传递给对应的角色，并刷新其持续时间。（此处数据为初始数据）额外能力：团膳套餐队伍中存在与自身属性或阵营相同的角色时触发：苍角消耗[涡流]发动[展旗]时，全队造成的冰属性伤害提升20%，持续22秒。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/01/82500917/6f4672eff16ad317dfa2dc176a242cdd_3406113533348923554.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/227.md","filePath":"game/zzz/role/[pkg].md"}'),d={name:"game/zzz/role/227.md"},_=Object.assign(d,{setup(i){return(p,n)=>(c(),a("div",null,[o(t(e))]))}});export{b as __pageData,_ as default};
