import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":758,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":758,"name":"赛斯","summary":"赛斯·洛威尔","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/a99297e48c27c1c09ccee2f8cf3d4745_8604815710532543749.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/c3a596936acc1de079653b634f164993_3868612104450693723.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/31/82500917/65638e28a79aff9f7520b14b72d8c7ee_1586378991603962836.png"},"attrs":[["稀有度","A"],["属性","电"],["特性","防护"],["阵营","刑侦特勤组"]],"destiny":[["LEVEL1英雄主义","[匪石之盾]提供的护盾及其上限提升30%；[匪石之盾]失效时，[核心被动：守望者]提供的异常精通提升效果额外维持10秒。团队行动的时候，赛斯永远是冲在最前面的人。「逮捕犯人就交给我吧！我的近身擒拿成绩是99分，扣掉的那一分是因为不小心扭伤了教官的手腕。」"],["LEVEL2年少轻狂","进入战场时，赛斯立刻为自身累积75%[意气]；发动[普通攻击：雷霆击-感电]命中敌人时，招式累积的电属性异常积蓄值提升35%。赛斯很容易被各种犯罪者小看。或许是因为他的眼神太过清澈，又或许是因为那无害的毛茸茸耳朵和尾巴——无论如何，所有看低他的人，最终都会付出相应的代价。"],["LEVEL3钝感力","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「像你这么厉害的治安官，肯定做什么都是靠自己吧？绝对没依赖其他人吧？」即使被这么说了，赛斯依然不动声色。直到半夜，他才忽然惊醒：「那家伙白天的话…莫非是在讽刺我？」"],["LEVEL4初心","[招架支援：迅雷盾]造成的失衡值提升25%。「放下你那愚蠢的善恶观。世上没有一种方式可以守护所有人。有时候，你必须做出选择。」「这就是我的选择，也是治安官最基本的职责——是你变了，哥哥。」"],["LEVEL5憧憬的距离","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2对于自己的兄长，赛斯曾抱有极度的憧憬和尊敬。但就像你知道的那样——「憧憬」是距离「理解」最遥远的距离。「总有一天，我会成为最厉害的治安官…让那家伙收回之前的话！」"],["LEVEL6理想主义","赛斯在[普通攻击：雷霆击-感电]中发动终结一击命中敌人时，额外造成自身500%攻击力的伤害，本次攻击必定触发暴击，且暴击伤害提升60%。少年人眼里的一切很清晰，黑白分明，善恶有别。一定会有人想告诉他世界的颜色是混沌的灰。但黑白分明善恶有别的世界…不值得追求吗？"]],"skill":[{"title":"普通攻击：雷霆击","desc":"点按发动：向前方进行至多四段的攻击，造成物理伤害和电属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/e7d720c3362762a331fc414cf0e4601b_7688469999335943139.gif"},{"title":"普通攻击：雷霆击-感电","desc":"[意气]高于75%时，长按发动：消耗75%[意气]向前方发动持续攻击，随后发动终结一击，造成电属性伤害；终结一击命中敌人时，将触发队伍上一位角色的[快速支援]；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/1934c38d826336349e139f051b761f59_1480969936229777125.gif"},{"title":"闪避：避障","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/46e051dcf8aff63fea02cf51807a4121_9078580855860980530.gif"},{"title":"冲刺攻击：电光突袭","desc":"闪避时，点按发动：向前进行盾冲攻击，造成物理伤害；招式发动期间受到的伤害降低40%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/9b8ff766e9bc7612ad2eab841bb20a0c_5145178431501233879.gif"},{"title":"闪避反击：以退为进","desc":"触发[极限闪避]后，点按发动：对前方敌人发动快速的下砸斩击，造成电属性伤害；招式发动后，长按衔接[强化特殊技：电光盾冲-高伏特]时，能够以更快的速度完成蓄力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/84dcc57973460a6e390fa40a650ab276_5559959400400028399.gif"},{"title":"快速支援：武力支援","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动快速的下砸斩击，造成电属性伤害；招式发动后，长按衔接[强化特殊技：电光盾冲-高伏特]时，能够以更快的速度完成蓄力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/1ac7e6b5d78b7df9fed7934ab6c1a95d_8746265129984991778.gif"},{"title":"招架支援：迅雷盾","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动时，会为自身累积25%[意气]；具有较为优秀的招架性能，承受高强度攻击时[支援点数]的消耗量降低招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/b2da9129954c9f8f1bebdbdfd715245a_3651313085614707312.gif"},{"title":"支援突击：治安裁决","desc":"发动[招架支援]后，点按发动：向前方发动突刺攻击，随后进行两次横扫斩击，造成电属性伤害；招式命中敌人时，将触发队伍上一位角色的[快速支援]；招式发动后，长按衔接[强化特殊技：电光盾冲-高伏特]时，能够以更快的速度完成蓄力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/d89b37e3d6b64f1de6c654907531c07b_7750364122142556611.gif"},{"title":"特殊技：电光盾冲","desc":"点按发动：向前方发动两次盾击，造成电属性伤害；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/4fc963b53e2c95169d83a158e84893b2_750108725237472831.gif"},{"title":"强化特殊技：电光盾冲-高伏特","desc":"能量足够时，点按发动：向前方发动连续斩击，造成大量电属性伤害；长按可以进行蓄力，蓄力完成后赛斯将向前方突进，发动高量级的连续斩击，造成大量电属性伤害；招式发动时，会为自身积攒75%[意气]；招式蓄力期间抗打断等级提升，受到的伤害降低40%；连续斩击期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/e87230e2ce60f96468e3b4f707d54e73_2989634187136394978.gif"},{"title":"连携技：最终制裁","desc":"触发[连携技]时，选择对应角色发动：向前方进行连续斩击，造成大量电属性伤害；招式发动后，会为自身累积75%[意气]；招式发动后，长按衔接[强化特殊技：电光盾冲-高伏特]时，能够以更快的速度完成蓄力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/cc1f4ec78c6aaa53a319348ea6cd13b2_1533757735806760382.gif"},{"title":"终结技：正义必胜","desc":"喧响等级达到[极]时，点按发动：向前方进行连续上挑攻击，随后发动终结一击，造成大量电属性伤害；招式发动时，会为自身累积75%[意气]，额外恢复3点支援点数；招式发动后，长按衔接[强化特殊技：电光盾冲-高伏特]时，能够以更快的速度完成蓄力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/82500917/d31e63a5bf4e2ad6bda58d12e62fba19_594623487357031039.gif"},{"title":"核心技","desc":"核心被动：守望者赛斯发动[强化特殊技：电光盾冲-高伏特]时，会获得等同于自身40%初始攻击力的护盾[匪石之盾]，最高不超过3,000点，持续25秒，10秒内最多触发一次；队伍内其他角色通过[普通攻击：雷霆击-感电]或[支援突击：治安裁决]所触发的[快速支援]或[连携技]入场时，会为该队友提供等同于赛斯40%初始攻击力的护盾[匪石之盾]，最高不超过3,000点，持续25秒，10秒内最多触发一次；拥有[匪石之盾]时，持有者的异常精通提升50点。（此处数据为初始数据）额外能力：意气风发队伍中存在与自身属性或阵营相同的角色时触发：赛斯发动[连携技：最终制裁]命中敌人或在[普通攻击：雷霆击-感电]中发动终结一击命中敌人时，目标的全属性积蓄抗性降低20%，持续20秒。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/19/82500917/fd6221f99b42288dd82b70364d0fb8a7_6968336200355409150.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/758.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/758.md"},_=Object.assign(i,{setup(d){return(p,n)=>(o(),c("div",null,[e(t(a))]))}});export{b as __pageData,_ as default};
