import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as e,o as a,G as o,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":759,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":759,"name":"简","summary":"简·杜","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/16/15559334/671b9f8d9227c19355e2eceb9f23ef28_7055750575833971395.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/da1060949bca29222c2a78d779ad555e_3525455305745400817.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/15/15559334/5e211be492e1c6c7466e9ca60b61fc9c_8458663124404287666.png"},"attrs":[["稀有度","S"],["属性","物理"],["特性","异常"],["阵营","刑侦特勤组"]],"destiny":[["LEVEL1犯罪顾问","[普通攻击：萨霍夫跳]的可使用次数上限增加一次，在[狂热]状态下，简的物理异常积蓄效率额外提升15%，同时每点异常精通将会使自身造成的伤害提升0.1%，最多提升30%。「犯罪顾问，是指研究犯罪行为学的顾问，不是精通如何犯罪的顾问。」简忽然凑近，眨了眨眼睛。「当然，你要理解成后者的话…我也不反对哦。」"],["LEVEL2近朱者赤","简攻击命中处于[啮咬]状态下的敌人，或队伍中任意角色对处于[啮咬]状态下的敌人触发[强击]时，将会无视其15%防御力，当[强击]伤害触发暴击时，其暴击伤害额外提升50%。凝视深渊时会被深渊回以凝视，这只是第一步。「然后要靠近它、进入它、理解它，这是第二步。」「最后一步…不要成为它。」"],["LEVEL3无名氏","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「无名氏，意味着我可以成为任何人。比如你最喜欢的人~」「或者，你最恐惧的人？」"],["LEVEL4因势利导","队伍中任意角色触发[强击]或[紊乱]效果时，全队造成的属性异常伤害提升18%，持续15秒。「是猎人还是猎物，往往取决于你选择的视角。不过，我更喜欢另一个简单的判断方式——」「输的人，就是猎物。」"],["LEVEL5囤积癖好","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2简的房间里永远塞满了各式各样的东西，其中最多的是速食食品。「这里很不错吧？到处都是触手可及的食物哦~」"],["LEVEL 6「肮脏」技巧","[狂热]状态下，简的暴击率提升20%，暴击伤害提升40%；队伍中任意角色对敌人施加[强击]效果时，简将直接进入[狂热]状态，并使[狂热心流]回复至上限，[强击]伤害触发暴击时，将会对目标发动一次额外攻击，造成等同于简1,600%异常精通的物理伤害。街头可不在乎手段脏不脏，只在乎结果赢没赢。「黑暗的欲望，才能推演出最痛切的弱点。」"]],"skill":[{"title":"普通攻击：跳步刃舞","desc":"点按发动：向前方进行至多六段的攻击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/c61b178c92f54be038bcce2a9886f25d_5727321968811021841.gif"},{"title":"狂热","desc":"简触发[极限闪避]、[招架支援]或发动招式对敌人造成伤害时，会累积[狂热心流]，当[狂热心流]累积至上限时，简将进入[狂热]状态；[狂热]状态下，简的物理异常积蓄效率提升25%，此外，若简的异常精通高于120点，每超过1点异常精通会使自身的攻击力提升2点，通过这种方式最多使自身的攻击力提升600点；简在[狂热]状态下发动招式造成伤害时，会消耗[狂热心流]，触发[极限闪避]或[招架支援]时，可以回复[狂热心流]、当[狂热心流]耗尽时，将退出[狂热]状态。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/0be2eebde58ad49be97182682acf8366_5484726368093008538.gif"},{"title":"普通攻击：萨霍夫跳","desc":"进入[狂热]状态时，简将获得一次[普通攻击：萨霍夫跳]的可使用次数；当有可使用次数时，长按发动：向前方发动快速的连续攻击，随后发动终结一击，造成物理伤害；连续攻击期间，保持长按可以延长招式的持续时间，松开可以提前发动终结一击；连续攻击发动期间抗打断等级提升，受到的伤害降低40%，终结一击发动期间具有无敌效果；在[狂热]状态下发动[普通攻击：萨霍夫跳]对敌人造成伤害时，可以回复[狂热心流]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/3d6496bf18bf0acb0355988d4b176b59_6115253179352001651.gif"},{"title":"闪避：残影","desc":"点按发动：快速的滑步闪避；招式发动期间拥有无敌效果；简拥有一次额外的闪避次数，在进入[狂热]状态前，简会交替使用两段闪避，在进入[狂热]状态后，简在闪避时可以穿过前方的敌人。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/27/15559334/10cb893b1f8b6af2aae11c5a0a06dacf_2676717507751729142.gif"},{"title":"冲刺攻击：刀刃跳","desc":"闪避时，点按发动：若衔接在一段闪避后，将向前方进行上挑斩击，造成物理伤害；若衔接在二段闪避后，将向前方进行连续斩击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/478c741e29b228da5df76ed7335eeb76_5371276190684423576.gif"},{"title":"冲刺攻击：虚像突刺","desc":"[狂热]状态下，闪避时，点按发动：向前方进行三次快速的突进斩击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/a78b5679cec44e1ee1e1f36ea8a8a1c5_3162024277790426469.gif"},{"title":"闪避反击：疾影","desc":"触发[极限闪避]后，点按发动：若衔接在一段闪避后，将对前方敌人发动多次斩击，随后向下突刺，造成物理伤害；若衔接在二段闪避后，将会跃起并对前方敌人发动三次连续攻击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/1d70274adee8e6ba68ee2d00ce6349f4_3350690822147997841.gif"},{"title":"闪避反击：疾影连舞","desc":"[狂热]状态下，触发[极限闪避]后，点按发动：对前方敌人发动多次快速斩击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/1d70274adee8e6ba68ee2d00ce6349f4_1806324275041987715.gif"},{"title":"快速支援：乌刺","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动多次斩击，随后向下突刺，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/3aa3c8c3d7d2ce982aa5e369a1e31c51_5730391566627831658.gif"},{"title":"快速支援：勾手跳","desc":"[狂热]状态下，当前操作中的角色被击飞时，点按发动：对前方敌人发动多次快速斩击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/e1fcf241c8abb4279e54f41b358e1945_8458909470698617015.gif"},{"title":"招架支援：最后防线","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/e71059e742344bc3fffc6adf67a15d9d_4087847317774346041.gif"},{"title":"支援突击：疾风扫","desc":"发动[招架支援]后，点按发动：腾空跃起后对敌人发动快速斩击，随后在前方大范围内再次发动横扫斩击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/8a4c7fa0d2beca9869d13fb4f84a94c7_1208604557888553621.gif"},{"title":"特殊技：掠空","desc":"点按发动：腾空跃起，向前方发动连续踢击后横扫，造成物理伤害；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/dd0cb36cca2c155ab1c6153c26710186_8020683416045250951.gif"},{"title":"强化特殊技：掠空-横扫","desc":"能量足够时，点按发动：腾空跃起，向前方发动多次连续踢击后横扫，造成大量物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/7850d7a6962d090e04057b886c4d4f25_8579132260499895506.gif"},{"title":"连携技：罪孽生花","desc":"触发[连携技]时，选择对应角色发动：对前方大范围敌人发动穿梭斩击，造成大量物理伤害；招式发动时，简将直接进入[狂热]状态，并使[狂热心流]回复至上限；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/ae961b31a9dd6cd929558c6c5336a724_5513797170463127915.gif"},{"title":"终结技：终幕演出","desc":"喧响等级达到[极]时，点按发动：对前方大范围敌人发动强力穿梭斩击，随后发动终结一击，造成大量物理伤害；招式发动时，简将直接进入[狂热]状态，并使[狂热心流]回复至上限；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/28/15559334/2c2921cdc38f5587f42665e7e7414cd4_2963354408561769706.gif"},{"title":"核心技","desc":"核心被动：洞察简攻击命中敌人时，会使其陷入[啮咬]状态，持续10秒；队伍中任意角色对处于[啮咬]状态下的敌人触发[强击]并施加[畏缩]效果时，[畏缩]效果的持续时间提升5秒，且[强击]伤害有概率触发暴击，基础暴击率为20%，暴击伤害为50%，简的每点异常精通会使该效果的暴击率额外提升0.1%。（此处数据为初始数据）额外能力：痛点队伍中存在其他[异常]角色或与自身阵营相同的角色时触发：简的物理异常积蓄效率提升20%，当敌人已经处于属性异常状态下时，简对其累积的物理异常积蓄值额外提升15%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/e6deebe158c2ad042c5e8fffb2b7601a_519926149593126276.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/759.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/759.md"},h=Object.assign(i,{setup(d){return(p,n)=>(a(),e("div",null,[o(t(c))]))}});export{l as __pageData,h as default};
