import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as o,o as a,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":840,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":840,"name":"柏妮思","summary":"柏妮思·怀特","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/76099754/c00700f00787444df5c7de8b09d81766_3155659045192705400.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/e6952e9ad28962251349800a850c5797_8571388244426416059.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/9c11d49f22a23196809d6737b5cc25e2_7937286902860105145.png"},"attrs":[["稀有度","S"],["属性","火"],["特性","异常"],["阵营","卡吕冬之子"]],"destiny":[["LEVEL1热络同心","[燃点]的上限从100点提升至140点，进入战场时，柏妮思额外累积40点[燃点]；[余烬]效果的伤害倍率提升，提升效果等同于柏妮思攻击力的100%；[余烬]效果累积的火属性异常积蓄值提升25%。「谁都有不够顺利的时候，很正常啦！」「别难过，凯撒，来一杯燃油饮特调怎么样？我加了很多很多糖哦！」"],["LEVEL2加量不加价","触发[余烬]效果时，将对敌人施加[热意洞穿]，最多叠加5层，持续6秒，重复触发时刷新持续时间；受到我方攻击时，目标每拥有一层[热意洞穿]，本次攻击的穿透率提升4%，最多提升20%。作为卡吕冬之子的调饮师，柏妮思乐于为所有朋友调制新艾利都最美味的燃油饮。当然，如果你是她的「敌人」…则会有机会喝到前所未有的新口味饮品。「是一生只能喝一次的超绝美味哦！」"],["LEVEL3天性乐观","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「柏妮思那家伙，不管什么时候都是一副元气满满的样子啊！就连刚起床都是！真不知道她从哪来这么多能量…」「更可怕的是，她还能把身边人都带动着积极起来！这简直快称得上是妖怪了吧！」「不过…我倒是不过厌就是啦…」——一位不愿意透露姓名的猪猪管理者"],["LEVEL4超绝燃油补给","[强化特殊技]或[支援攻击]命中敌人时，招式的暴击率提升30%；[强化特殊技：灼热摇荡法·双份]中，火焰喷射的持续时间上限提升1秒。「喜欢我调的饮料？谢谢你，大钢牙也很喜欢~那家伙一顿能吃两百升呢！」「哦，大钢牙就是我们的那辆大卡车啦！」「想尝尝它的特供燃油吗？很火热的哦~」"],["LEVEL5冰与火之舞","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2在调制饮品的时候，冰块也是非常重要的一环。毕竟没有极致的冰冷，就难以村托出极致的火热。「这种火与冰交融的感觉~真令人沉迷呢！」"],["LEVEL6盛焰邀约","柏妮思发动[强化特殊技：灼热摇荡法·双份]命中敌人时，将额外触发特殊的[余烬]效果，对目标造成等同于柏妮思60%攻击力的火属性伤害，0.5秒内最多触发1次，无需消耗[燃点]；发动[强化特殊技：灼热摇荡法·双份]命中敌人后，招式发动期间，[强化特殊技：灼热摇荡法·双份]、特殊的[余烬]效果、[灼烧]效果将无视目标25%火属性伤害抗性；在[强化特殊技：灼热摇荡法·双份]中发动火焰冲击命中处于[灼烧]状态下的敌人时，能够额外结算一次[灼烧]伤害，伤害效果等同于原本的1,800%，同一目标20秒内最多触发一次。好像从来没有谁见过柏妮思真正生气的样子。「相信我，你们不会想要看到的~」——来自一位正在大钢牙上准备入睡的司机"]],"skill":[{"title":"普通攻击：炽焰直调式","desc":"点按发动：向前方进行至多五段的攻击，造成物理伤害和火属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/39e2c5d809ef17cbaf058caeedb628dc_1280971224545009528.gif"},{"title":"普通攻击：炽焰搅拌式","desc":"[燃油特调]状态下，长按发动：持续回旋并向周身喷射火焰，随后发动终结一击，造成火属性伤害，保持长按可以延长喷射的持续时间；招式发动时，将会消耗20点[燃点]；招式发动期间抗打断等级提升；终结一击发动后，点按可以直接衔接发动[强化特殊技：灼热摇荡法·双份]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/b01e8175b0355f52f8e4d8324e8597c8_4197179538730390043.gif"},{"title":"闪避：焰影疾行","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/be237354b142c730bfb4646c55abc76a_795223934138867343.gif"},{"title":"冲刺攻击：危险发酵式","desc":"闪避时，点按发动：挥舞喷射器向前方进行突进攻击，造成火属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/8e38d8b8f064278f3297419fac1474dd_8673138494022927192.gif"},{"title":"闪避反击：摇荡闪","desc":"触发[极限闪避]后，点按发动：对前方敌人发动连续攻击并喷射火焰，造成物理伤害和火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/dcd37fb98d88bb4755dd1f47f5318ecb_1545080011188796601.gif"},{"title":"快速支援：提神特饮","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动连续攻击并喷射火焰，造成物理伤害和火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/125532fa3ae92c62e3c8ceb2afdaa415_4047326698089033467.gif"},{"title":"招架支援：烟熏油盅","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/72859efdae08369792d2fdf417b1660c_8400116267534574002.gif"},{"title":"支援突击：灼焰甘露","desc":"发动[招架支援]后，点按发动：腾空跃起，快速旋转前进并向周身喷射火焰，造成火属性伤害；招式发动后，点按可以直接衔接发动[强化特殊技：灼热摇荡法·双份]。招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/342cab2d90dba2ca613966dd34b73484_4147652900628039393.gif"},{"title":"特殊技：灼热熟成法","desc":"点按发动：向前方发动一次火焰冲击，造成火属性伤害；长按可以进行蓄力，提升招式的威力；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/2a7cb786e5b016f357ac9540c9eead39_4382952820045096391.gif"},{"title":"强化特殊技：灼热摇荡法","desc":"能量足够时，点按发动：使用单手喷射器持续喷射火焰，随后向前方直线范围发动一次火焰冲击，造成大量火属性伤害，保持长按可以持续消耗能量并延长喷射的持续时间；持续喷射期间拖曳摇杆，可以发动侧身闪避，向对应方向调整身位；持续喷射期间抗打断等级提升，受到的伤害降低40%；侧身闪避和火焰冲击发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/b9b7167263616048cbdb44b04133d5bf_6717443036664158417.gif"},{"title":"强化特殊技：灼热摇荡法·双份","desc":"[强化特殊技：灼热摇荡法]发动期间，能量足够时，点按发动：使用双手喷射器持续喷射火焰，随后向前方直线范围发动一次强力的火焰冲击，造成大量火属性伤害，保持长按可以持续消耗能量并延长喷射的持续时间；喷射发动瞬间和火焰冲击发动期间拥有无敌效果；持续喷射期间抗打断等级提升，受到的伤害降低40%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/f389f771210d1d8e15b14b991a0182d7_6307784741661166617.gif"},{"title":"连携技：燃油熔焰","desc":"触发[连携技]时，选择对应角色发动：快速突进并向周身喷射火焰，随后向前方扇形范围发动一次火焰冲击，造成大量火属性伤害；招式发动后，点按可以直接衔接发动[强化特殊技：灼热摇荡法·双份]。招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/c0f0516c896e48c9b51e617bb25defc2_630263464839549033.gif"},{"title":"终结技：纵享盛焰","desc":"喧响等级达到[极]时，点按发动：快速突进后跃向空中，对前方大范围区域持续喷射火焰，造成大量火属性伤害。跃向空中时，将触发[快速支援]，若响应[快速支援]，柏妮思会延长火焰喷射的持续时间；招式发动时，回复50点[燃点]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/1abe93adf50437e197c8ef1f1c460208_5199230972249967311.gif"},{"title":"","desc":"核心被动：燃油特调柏妮思消耗能量值时，会为自身累积[燃点]，最多累积100点；每消耗1点能量值，能够累积1.4点[燃点]，当[燃点]累积至50点时，柏妮思将进入[燃油特调]状态，直到[燃点]耗尽；进入战场时，柏妮思立刻累积100点[燃点]；[燃油特调]状态下，柏妮思发动[普通攻击：炽焰搅拌式]、[强化特殊技]、[连携技]或[终结技]命中敌人时，将对目标施加[灼伤]效果，直到柏妮思退出[燃油特调]状态；队伍中其他角色攻击命中处于[灼伤]状态下的敌人时，会触发[余烬]效果，消耗8点[燃点]，对目标造成等同于柏妮思175%攻击力的火属性伤害并累积火属性异常积蓄值，1.5秒内最多触发一次；每拥有10点异常精通，[余烬]效果造成的伤害提升1%，最多提升30%；[余烬]效果造成的伤害被视为[支援攻击]伤害。（此处数据为初始数据）额外能力:星火燎原队伍中存在其他[异常]角色或与自身阵营相同的角色时触发：柏妮思发动[普通攻击：炽焰搅拌式]、[强化特殊技：灼热摇荡法]、[强化特殊技：灼热摇荡法·双份]命中敌人，或触发[余烬]效果时，招式累积的火属性异常积蓄值提升65%；队伍中任意角色对敌人施加[灼烧]效果时，该效果的持续时间提升3秒。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/15559334/c759fb2ef7b6dade8c2c2ba7cb4e1bdd_1303335237482849727.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/840.md","filePath":"game/zzz/role/[pkg].md"}'),d={name:"game/zzz/role/840.md"},_=Object.assign(d,{setup(i){return(p,n)=>(a(),o("div",null,[e(t(c))]))}});export{l as __pageData,_ as default};
