import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":950,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":950,"name":"莱特","summary":"莱特","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/76099754/97be522aa2bce70b13167df8b42e176f_6973279715938714866.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/8f590018849ca6efe2ed05acaa9c3b59_842747339959687381.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/18/76099754/637798ed499a2f86e416b3bb2b0203f9_7665791603372082688.png"},"attrs":[["稀有度","S"],["属性","火"],["特性","击破"],["阵营","卡吕冬之子"]],"destiny":[["LEVEL1常胜冠军","在[核心被动：助燃剂]所施加的减益效果中，[溃败]效果将使失衡持续时间提升5秒，冰属性伤害抗性和火属性伤害抗性将额外降低10%；在轻拳起攻或刺拳连击期间耗尽[士气]，自动衔接发动强力终结一击时，强力终结一击造成的伤害提升30%。常胜冠军的胜利至关重要。与对面最强的战士决斗，为团长解决威胁——同时为团队带来巨大的士气鼓舞。对他来说，唯有胜利值得赞颂。「常胜冠军不能倒下。」"],["LEVEL2红围巾","通过[核心被动：助燃剂]对敌人施加[溃败]效果时，目标的失衡易伤倍率提升25%；[额外能力：斗志昂扬]中由[昂扬]提供的冰属性伤害和火属性伤害增益效果提升至原本的120%。作为卡吕冬之子的「红围巾」，莱特在外环非常有名。不只是因为他的拳头，也因为…某些说话风格。「虽然我乐得清闲…不过没有我的话，你们也搞不定吧？」"],["LEVEL3佣兵团长","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「我大概是全世界最不适合当团长的人吧。」"],["LEVEL4墨镜","莱特位于后场时，前场角色的能量自动回复效率提升10%；莱特进入[士气喷发]状态时，为后场角色回复4点能量，18秒内最多触发一次。「问我为什么要带墨镜？恩…因为我晕血啊，大概吧。」"],["LEVEL5决策者","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「给我命令，我来照做。就这么简单。」莱特推了推自己的墨镜，笑得漫不经心。「别再让我来做决定就行。」"],["LEVEL6幸存者","莱特的[士气]回复效率提升至原本的200%；莱特在[普通攻击]、[闪避反击]、[特殊技]、[强化特殊技]、[快速支援]、[支援突击]、[连携技]、[终结技]中发动重击命中敌人时，将会触发[火焰冲击]效果，造成250%攻击力的火属性伤害，每个敌人8秒内最多触发一次；若莱特的冲击力高于170点，每超过1点冲击力会使[火焰冲击]的倍率额外提升5%，最多提升500%；在轻拳起攻或刺拳连击期间耗尽[士气]，自动衔接发动强力终结一击命中敌人时，可以忽略冷却时间，额外触发一次[火焰冲击]效果。重来一次，他绝不会选择成为唯一的幸存者。可惜命运向来无法选择。"]],"skill":[{"title":"普通攻击：L式轰鸣拳","desc":"点按或长按发动：向前方进行至多五段的打击，造成物理伤害和火属性伤害；在[普通攻击]第三段发动期间，拖曳摇杆，同时连点或长按，可以持续发动连续体术追击，松开摇杆，则衔接后继第四段[普通攻击]；在[普通攻击]第五段轻拳起攻期间，拖曳摇杆，同时连点或长按，可以循环发动轻拳起攻和第四段[普通攻击]，松开摇杆，则衔接后继刺拳连击；保持连点或长按时，可以延长刺拳连击的持续时间；停止连点或长按后，或刺拳连击至持续时间上限后，将向前方发动终结一击，终结一击期间拥有无敌效果；[普通攻击]第五段发动期间，莱特受到的伤害降低40%；[士气喷发]状态下，[普通攻击]第五段发动期间抗打断等级提升，伤害降低效果提升至80%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/d11a24f07db9bf5856be6c9d35eecb6e_4491161451160970822.gif"},{"title":"L式轰鸣拳-体术追击","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/e6aa21e5a2565561ef64d6f211552259_1908673352778796754.gif"},{"title":"L式轰鸣拳-轻拳起攻","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/6db6d2a367190a0c720c632c4dc44744_1746261803023282019.gif"},{"title":"L式轰鸣拳-刺拳连击","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/23838a9400e90056cd79ac44b89824f2_5939995059950404197.gif"},{"title":"闪避：匿影滑步","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。角色发动攻击期间，点按发动：向摇杆方向垫步闪避；发动该招式不会打断[普通攻击]的连击段数；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/d1c7c0b1a7bd44af84f613c3678064f8_5900066044234378770.gif"},{"title":"冲刺攻击：骸突","desc":"闪避时，点按发动：向前方进行快速打击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/2c935a3a69994f71778ade93e047680b_9086709665298768541.gif"},{"title":"闪避反击：烈闪","desc":"触发[极限闪避]后，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果；招式发动后，点按或长按可以直接衔接发动第五段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/c2ef4cc75a383028b1086aba63431212_4014161562295807610.gif"},{"title":"垫步闪避","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/535f90bc564964702f3caf8d4a42587e_6485591564600058715.gif"},{"title":"垫步闪避接特殊技","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/caf1468194c7180f98f8899fcfd8d453_1324137968388802617.gif"},{"title":"垫步闪避接强化特殊技","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/bd60a59eec7d7bfe9cafc548f7314a96_2439467337584794288.gif"},{"title":"快速支援：烈闪-守","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果；招式发动后，点按或长按可以直接衔接发动第五段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/9a9da97de3d4ebe8ebcb59dd10122629_5697068962261169849.gif"},{"title":"招架支援：瞬破","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/1c2623e5f1f37a23878d0f652b1d053b_39497483379012985.gif"},{"title":"支援突击：骸突-刺","desc":"发动[招架支援]后，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果；招式发动后，点按或长按可以直接衔接发动第五段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/aa471123cfc6d69ad6eb76dd4def5c56_2028764203383631964.gif"},{"title":"特殊技：V式日轮升拳","desc":"点按发动：使用上勾拳向前方进行打击，造成火属性伤害；招式发动期间抗打断等级提升。垫步闪避期间，点按发动：使用组合拳向前方进行打击，造成火属性伤害；发动该招式不会打断[普通攻击]的连击段数；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/fc97851957ba9a758b284e8540d313ab_7623348164313532989.gif"},{"title":"强化特殊技：V式日轮升拳-全冲程","desc":"能量足够时，点按发动：使用上勾拳离地跃起并向前方进行强力打击，造成大量火属性伤害；招式发动期间，再次点按可以额外消耗能量，发动强力追击，造成大量火属性伤害；招式发动期间拥有无敌效果，招式发动后，点按或长按可以直接衔接发动第四段[普通攻击]。垫步闪避期间，能量足够时，点按发动：使用组合拳向前方进行强力打击，造成大量火属性伤害；发动该招式不会打断[普通攻击]的连击段数；招式发动期间拥有无敌效果；招式发动后，点按或长按可以直接衔接发动第五段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/88603dfec83f9a2a0e972a985a80a7f0_9144655959728611938.gif"},{"title":"V式日轮升拳-全冲程-强力追击","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/21/158441880/aa121b08cf69a1f97721b86910150f5e_785044012130919881.gif"},{"title":"连携技：V式灼日炎","desc":"触发[连携技]时，选择对应角色发动：对前方小范围敌人发动强力打击，造成大量火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/f87dfe9d7f8538728d7d01cba68762bf_6694045023860559373.gif"},{"title":"终结技：W式桂冠终火","desc":"喧响等级达到[极]时，点按发动：对身周大范围敌人发动强力打击，造成大量火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/4e045aa14806e0b49d8b5676f11f0eb5_5385547146854252416.gif"},{"title":"核心技","desc":"核心被动：助燃剂莱特会随时间自动获得[士气]，每秒回复2.9点，队伍中任意角色每消耗1点能量值，额外为莱特回复0.26点[士气]，莱特最多拥有100点[士气]；[士气]回复至80点时，下一次换入前场将直接发动[快速支援]；[士气]大于等于80点后，莱特发动第五段[普通攻击]时，将进入[士气喷发]状态，提升[普通攻击]第五段中轻拳起攻、刺拳连击和终结一击的威力；威力提升后，招式命中敌人时，会持续消耗[士气]，每消耗10点[士气]，莱特的冲击力提升1%，最多提升10%，持续6秒；[士气喷发]状态下，莱特不再随时间自动获得[士气]，轻拳起攻或刺拳连击期间耗尽[士气]时，会自动衔接发动更加强力的终结一击；[士气喷发]状态下，轻拳起攻或刺拳连击命中敌人时，目标的冰属性伤害抗性和火属性伤害抗性降低15%，持续30秒；[士气喷发]状态下，终结一击命中敌人时，会对目标施加[溃败]效果，使其进入失衡状态后的失衡持续时间提升3秒，从失衡状态恢复前[溃败]效果对同一目标最多触发一次；在[普通攻击]第五段中发动终结一击命中敌人、换回后场或[士气]耗尽时，莱特将退出[士气喷发]状态，尚未消耗的[士气]会被保留。（此处数据为初始数据）额外能力：斗志昂扬队伍中存在[强攻]角色或与自身阵营相同的角色时触发：[士气喷发]状态下，莱特发动第五段[普通攻击]命中敌人时，全队获得一层[昂扬]，最多叠加20层，持续30秒，重复触发时刷新持续时间；每拥有一层[昂扬]，代理人造成的冰属性伤害和火属性伤害提升1.25%，获得[昂扬]时，若莱特的冲击力高于170点，每超过10点冲击力会使每层[昂扬]提供的增益效果额外提升0.25%；队伍中任意角色发动[连携技]时，会刷新全队[昂扬]的持续时间；[昂扬]最多使代理人造成的冰属性伤害和火属性伤害提升75%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/17/158441880/89b4e8e0f33a222d73bf5f04293994c6_7114306064262162942.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/950.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/950.md"},h=Object.assign(i,{setup(d){return(p,n)=>(o(),c("div",null,[e(t(a))]))}});export{l as __pageData,h as default};
