import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as a,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":997,"idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":997,"name":"悠真","summary":"浅羽 悠真","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/09/82500917/7477bdb2d366fd83b30671812c08e2eb_7866771727764017477.jpg","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/efc17a4415b0094a4a16100abc90106d_4027085636166531524.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/09/82500917/a2be54f9ab4fcb1a67b24fa5ab546e98_8039570205873872089.png"},"attrs":[["稀有度","S"],["属性","电"],["特性","强攻"],["阵营","H.S.O.S.6"]],"destiny":[["LEVEL1「意气风发」","[普通攻击：甲乙矢]中，[电囚]效果的叠加层数上限提升至14层；[电壶]被激活时，将会向目标连续发射2支[甲乙矢]。「我才不像某些死心眼的家伙一样，总之执着于正面战。」「不管是明枪还是暗箭，能让我早点下班的都是好孩子~」"],["LEVEL2挥霍","发动[连携技]或[终结技]时，悠真获得7层[电掣]，最多叠加7层；拥有[电掣]时，[冲刺攻击：飞弦·斩]造成的伤害提升50%；[冲刺攻击：飞弦·斩]结束后，消耗1层[电掣]。「别看我这样，我其实很弱很弱的~」悠真懒洋洋地趴在了办公桌上。「所以这次的任务，换个人怎么样?」"],["LEVEL3白色谎言","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「…放心，暂时死不了。」他笑着抹了一把嘴角溢出的鲜血，再次架起了弓「我可是要遗千年的。」"],["LEVEL4偶有充盈","[电囚]效果的持续时间提升至20秒；[冲刺攻击：飞弦·斩]命中敌人时，悠真获得30点喧响值，同一招式内最多触发一次；发动[终结技]时，将为场上所有敌人施加满层[电囚]效果。悠真偶尔也有干劲满满的时候——在距离下班还有五分钟之前。"],["LEVEL5无人知晓","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「我想要假期，要自由，要每天都开开心心——」「骗你的。我想活着。」"],["LEVEL6会心","[甲乙矢]命中处于失衡状态或属性异常状态下的敌人后，悠真将无视其15%电属性伤害抗性，持续12秒；[甲乙矢]每命中同一名敌人12次，将额外触发1次电磁爆炸，对目标造成悠真1500%攻击力的电属性伤害。「师父曾教过我很多事。如何拉弓，如何用刀，如何…在这个世界里幸存下来。」「…你问后来?」「后来啊…他也离开我了。」"]],"skill":[{"title":"普通攻击：穿云","desc":"点按发动：向前方进行至多五段的穿透攻击，造成物理伤害和电属性伤害；第五段攻击命中敌人时，可生成2枚[电壶]；发动[连携技]后，悠真下一次发动[普通攻击：穿云]第五段命中敌人时，可生成6枚[电壶]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/fc755be07b79729bb371ae6c9430af59_3599808544778639986.gif"},{"title":"普通攻击：穿云·移形","desc":"[普通攻击：穿云]发动期间，拖曳摇杆并点按发动：发动远程穿透攻击，并向拖曳方向调整身位，对前方敌人造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/b9bd209ff87a868c953ac1d079a0a507_1963740043493562546.gif"},{"title":"普通攻击：落羽","desc":"第五段[普通攻击：穿云]、[特殊技]、[强化特殊技]或[连携技]发动后，长按发动：立即向前方敌人发射[落羽]，造成电属性伤害。长按瞄准目标，松开发动：引弓瞄准敌人后，向目标发射[落羽]，造成电属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/15/82500917/8fb2d5181c4b83aff5a597bf73688105_9192151770545409138.gif"},{"title":"普通攻击：甲乙矢","desc":"第五段[普通攻击：穿云]/[强化特殊技]/[连携技]命中敌人或地面时，可生成2/6/6枚[电壶]；若场上不存在[电壶]，队伍中任意角色对敌人施加属性异常效果时，可生成6枚[电壶]，12秒内最多触发一次；[电壶]同一时间内最多存在6枚，悠真发射[落羽]命中敌人时，若场上存在[电壶]，则会将其激活，向被[落羽]命中的目标发射[甲乙矢]，造成电属性伤害；[甲乙矢]命中敌人时，会为其施加[电囚]效果，最多叠加8层，持续10秒，重复触发时刷新持续时间，[电囚]效果大于等于2层时，将目标身上生成[十文字]标记。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/278dec790d96212ea0cbadb69e36c32f_2201656923982941824.gif"},{"title":"普通攻击：落羽（瞄准）","desc":"","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/15/82500917/022bf218e5b6c40fcee3d5ca6698bcc6_775098639730004646.gif"},{"title":"闪避：瞬闪","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/d163ac07ec8c3a83af8954188759952a_1125662827832461709.gif"},{"title":"冲刺攻击：飞弦","desc":"闪避时，点按发动：向前方进行多段穿透攻击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/a3cb08431259909baeaffd255c786065_5416837593528310525.gif"},{"title":"闪避反击：藏锋","desc":"触发[极限闪避]后，点按发动：对前方敌人发动穿透攻击，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/0ec5798a0289c8456888aa8a10b61fbe_2615025223396662023.gif"},{"title":"冲刺攻击：飞弦·斩","desc":"当场上存在拥有[十文字]标记敌人时，悠真会进入[醒觉]状态，并将[冲刺攻击：飞弦]替换为[冲刺攻击：飞弦·斩]。[醒觉]状态下，闪避时，点按发动：快速突进至距离自身最近的带有[十文字]标记的敌人身前，发动强力斩击，造成电属性伤害；斩击命中目标时会消耗2层[电囚]效果，[电囚]效果少于2层时，将消除[十文字]标记；在[十文字]标记耗尽前，连续点按，可继续发动[冲刺攻击：飞弦·斩]；当场上不存在带有[十文字]标记的敌人时，悠真将退出[醒觉]状态；[醒觉]状态下，[普通攻击：落羽]发动后，点按可以快速衔接发动[冲刺攻击：飞弦·斩]；招式发动期间抗打断等级提升，受到的伤害降低40%。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/4de24d25dbf43a58f70b78eb18fd133c_5561168409378398478.gif"},{"title":"快速支援：穿弦","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动穿透攻击，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/62a89ca9d6a9082ca01cc36d62677523_2268581269379637513.gif"},{"title":"招架支援：构身","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/ace433c92a65c70e67ddbcb34a515185_8842720365617189567.gif"},{"title":"支援突击：构身·斩","desc":"发动[招架支援]后，点按发动：对前方敌人发动连续斩击，造成电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/e1db812c5d9a1a544713e347e412127e_6299684478467159837.gif"},{"title":"特殊技：天罗","desc":"点按发动：向前方发射箭矢，造成电属性伤害；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/af214d0bab0ef286cbf89ef919f6f3dc_4449433513031501751.gif"},{"title":"强化特殊技：地网","desc":"能量足够时，点按发动：向前方发射飞刃和强化箭矢，造成大量电属性伤害；强化箭矢命中敌人或地面时，可生成6枚[电壶]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/c91ad60ce0b63730266b38877fa5613e_2246146946704252996.gif"},{"title":"连携技：会·离","desc":"触发[连携技]时，选择对应角色发动：对前方大范围敌人发动穿透攻击，并发射强化箭矢，造成大量电属性伤害；箭矢命中地面时，可生成6枚[电壶]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/f19bf7c96f0e82f5080177f142187650_4741092940431401658.gif"},{"title":"终结技：残心","desc":"喧响等级达到[极]时，点按发动：对前方敌人发动强力穿透攻击，造成大量电属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/5e50aa3492bd28b91a506e0c3bcf559f_8213340065422395810.gif"},{"title":"","desc":"核心被动：破晓[冲刺攻击：飞弦·斩]的暴击率提升10.6%，悠真发动[冲刺攻击：飞弦·斩]命中敌人并触发暴击时，将获得1层[锋芒]，同一招式最多获得3层，[锋芒]最多叠加6层，持续5秒，重复触发时刷新持续时间；每层[锋芒]会使[冲刺攻击：飞弦·斩]造成的暴击伤害提升6%。额外能力：超频队伍中存在[击破]角色或[异常]角色时触发：悠真攻击命中处于失衡状态或属性异常状态下的敌人时，自身造成的伤害提升40%；悠真发射[落羽]命中处于失衡状态或属性异常状态下的敌人时，会为其施加2层[电囚]效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/08/82500917/67553bf65e485ad0ee2798cb8c92d2ec_2884967780022774364.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/997.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/997.md"},h=Object.assign(i,{setup(d){return(p,n)=>(o(),a("div",null,[e(t(c))]))}});export{l as __pageData,h as default};
