import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"凯撒","idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":801,"name":"凯撒","summary":"凯撒·金","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/10/08/76099754/45788df6a0a15baf27d33f11a93e8c04_1410824794677572273.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/1f7a27cebb1310f285c95cf41ab24b9f_337159011377819947.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/12/82500917/72f098dd58529b396c21f3580cdd5a95_5221616573435155700.png"},"attrs":[["稀有度","S"],["属性","物理"],["特性","防护"],["阵营","卡吕冬之子"]],"destiny":[["LEVEL1楔式进军","进入接战状态或者换入前场时，凯撒立即为全队提供[荣光之盾]，300秒内最多触发一次；[荣光之盾]存在期间，持有者附近5米范围内敌人的全属性伤害抗性降低15%。「大老爹教过我，这时候该以强大的兵力构成楔子般的队形，向敌方最薄弱之处进行攻击——」「当然，是由我来打头阵！」"],["LEVEL2异域传承","[核心被动：坚韧之壁]中，[荣光之盾]存在期间，凯撒的能量回复效率提升10%，持有者的攻击力提升效果提升至原本的150%。凯撒的战斗方式与新艾利都或是外环的风格都略有不同，继承自流浪者集团最厉害的武者们。在恰当的时候收敛锋芒，在恰当的时候一击毙命——这向来是流浪者们的生存之道。而凯撒一直是个好学生。"],["LEVEL3绝对信任","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2一旦认可某个人之后，凯撒就会把对方划到自己的麾下，并予以其绝对的信任，毫不设防…一如她现在对待你那样。"],["LEVEL4阿瑞斯攻城锤","凯撒发动[连携技]或[终结技]时,额外回复3点[支援点数]；凯撒的能量值不足20点时，可以消耗1点[支援点数]作为代替，发动[强化特殊技：超强力盾击]，代替效果5秒内最多触发一次。凯撒在流浪者集团导师教育下学会的战术之一。「无论如何，我会战到最后一刻！」"],["LEVEL5浪漫征伐","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2与外表不同，凯撒拥有特别的浪漫情怀。例如她总是对一些恋爱戏码充满了好奇心。但如果你和她更「深入」地聊这个话题…凯撒则会难得红了脸。"],["LEVEL6霸主之志","[强化特殊技：超强力盾击]和[支援突击]必定触发暴击，招式造成的伤害提升50%，对主要目标造成的伤害额外提升50%；凯撒发动[强化特殊技：超强力盾击]或[支援突击]时，自身暴击率提升30%，暴击伤害提升60%，持续15秒。「我曾只看向前方。我以为只要自己够强，就没有办不到的事。」「但要成为优秀的霸主，这还远远不够。」「我还需要守护好后方的同伴——他们才是我最大的底气。」"]],"skill":[{"title":"普通攻击：横行斩打","desc":"点按发动：向前方进行至多六段的斩击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/cafa3d490b626bb14b3d2527fc88da93_6930185892408860120.gif"},{"title":"普通攻击：此路不通！","desc":"[支援点数]足够时，长按发动：消耗1点[支援点数]，丢出手中的盾牌对四周进行攻击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/44660a3f445dd84164cf95644e794ce5_8519040741230510881.gif"},{"title":"闪避：漂移","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/5fc646f3f4927916ba77b0670240947a_839583312049121405.gif"},{"title":"冲刺攻击：猪突猛进","desc":"闪避时，点按发动：使用盾牌向前方进行打击，造成物理伤害。招式发动期间自身受到的伤害降低40%，受到敌人攻击时将持盾格挡并被击退，随后点按或，可以触发[防御反击]，发动[特殊技：喧嚣直刺]或[强化特殊技：超强力盾击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/3ee6abdc29a51c77b30801e666cb0a90_6582125123655528934.gif"},{"title":"闪避反击：以牙还牙","desc":"触发[极限闪避]后，点按发动：对前方敌人发动刺击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/ccfb2e2ee40464f36d38992ba6bfd9eb_3177538689361890621.gif"},{"title":"快速支援：变道支援","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动刺击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/a234430f49be92ae8a68bdebfab3b0cc_3790448127660391158.gif"},{"title":"招架支援：守御之盾","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；具有较为优秀的招架性能，承受高强度攻击时[支援点数]的消耗量降低；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/ab37c20170b88744ac72fa7f4a5cb6e8_7220735501925668856.gif"},{"title":"支援突击：支援之锋","desc":"发动[招架支援]后，点按发动：丢出手中的盾牌对前方敌人发动强力旋转攻击，随后发动斩击，造成物理伤害；招式发动后点按或，可以触发[防御反击]，发动[特殊技：喧嚣直刺]或[强化特殊技：超强力盾击]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/dd5d8e3af7e2ba593dbdbca57bbe88cf_1868712138325913272.gif"},{"title":"特殊技：震荡盾击","desc":"点按发动：使用盾牌向前方进行打击，造成物理伤害；若在招式发动初期的较短时间内受到敌方攻击，将触发[精准格挡]，抵挡此次攻击伤害，并自动衔接发动[特殊技：喧嚣直刺]；招式发动后，凯撒将进入格挡架势，期间自身受到的伤害降低40%，受到敌人攻击时将持盾格挡并被击退，随后点按或，可以触发[防御反击]，发动[特殊技：喧嚣直刺]或[强化特殊技：超强力盾击]；触发[防御反击]时，招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/a40c10752f12bd1e459e01670636ae42_1975521666313303376.gif"},{"title":"特殊技：喧嚣直刺","desc":"[特殊技：震荡盾击]发动后，点按发动：向前方进行刺击，造成物理伤害；未触发[精准格挡]和[防御反击]时，招式发动期间抗打断等级提升；触发[精准格挡]或[防御反击]时，招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/a335a838de4533753dfc1137e5c1a6bc_3885754648315744477.gif"},{"title":"强化特殊技：招架反击","desc":"能量足够时，点按发动：使用盾牌向前方进行打击，随后发动强力刺击，造成大量物理伤害；若在招式发动初期受到敌方攻击，或通过盾牌打击触发[破招]时，将触发[精准格挡]，抵挡此次攻击伤害，并挥盾反击；触发[精准格挡]后，点按，可以发动[强化特殊技：超强力盾击]，且无需消耗能量；招式发动后，凯撒将进入格挡架势，期间自身受到的伤害降低40%，受到敌人攻击时将持盾格挡并被击退，随后点按或，可以触发[防御反击]，发动[特殊技：喧嚣直刺]或[强化特殊技：超强力盾击]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/b82d4e2807c8cf20430fc2f4a6708792_6795737068542209698.gif"},{"title":"强化特殊技：超强力盾击","desc":"[强化特殊技：招架反击]发动后，能量足够时，点按发动：使用盾牌向前方进行打击，造成大量物理伤害；若在招式发动初期受到敌方攻击，或通过盾牌打击触发[破招]时，将触发[精准格挡]，抵挡此次攻击伤害，并返还发动招式所消耗的能量；以[精准格挡]或[防御反击]的方式发动时，无法再次触发[精准格挡]；招式发动期间拥有无敌效果。攻防转换凯撒触发[精准格挡]、[防御反击]或[招架支援]时，自身冲击力提升9%，持续3秒。（此处数据为初始数据）","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/b5ec5c8ea34dc64b1bcb7e72c73aa5e8_8948200957681614952.gif"},{"title":"连携技：路怒震打","desc":"触发[连携技]时，选择对应角色发动：对前方范围敌人发动强力下落打击，造成大量物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/84d1835486eaf90074d03b4a2a2eb300_6248347693041822233.gif"},{"title":"终结技：暴君猛击","desc":"喧响等级达到[极]时，点按发动：丢出手中的盾牌对前方敌人发动强力旋转攻击，随后发动下落打击，造成大量物理伤害；命中持有护盾的敌人时，招式造成的失衡值提升45%；（此处数据为初始数据）招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/8b807b31d759886d94eef1bf92578669_2384313409743372404.gif"},{"title":"核心技","desc":"核心被动：坚韧之壁凯撒发动[强化特殊技：招架反击]、[连携技]、[终结技]或以[防御反击]的方式发动[强化特殊技：超强力盾击]时，为全队提供等同于自身300%初始冲击力+200点的护盾[荣光之盾]，持续60秒，[荣光之盾]由前场角色持有，护盾值为全队共享，单次受到的伤害不会超过护盾值；[荣光之盾]存在期间，持有者的抗打断等级提升；[荣光之盾]存在期间，持有者的攻击力提升40点，[荣光之盾]效果结束后，该增益效果仍然保留，持续5秒。（此处数据为初始数据）额外能力：战意激昂队伍中存在其他可发动[招架支援]的角色或与自身阵营相同的角色时触发：凯撒触发[精准格挡]、[防御反击]、[招架支援]或发动[普通攻击：此路不通！]时，对自身附近7米范围内的敌人施加减益效果，使全队对其造成的伤害提升25%，持续30秒。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/09/14/82500917/df9bb59187b93d547620a61ac03148c2_4375221280168835082.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/凯撒.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/凯撒.md"},_=Object.assign(i,{setup(d){return(p,n)=>(o(),c("div",null,[e(t(a))]))}});export{l as __pageData,_ as default};
