import{_ as a}from"./chunks/index.VAya1KE4.js";import{c,o as e,G as o,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"比利","idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":371,"name":"比利","summary":"比利·奇德","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/31/76099754/149feef57a5210d07d646ff3dec3581f_214202686184867036.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/539bc247c906ce6d5a032dfc69b7185d_3514757821736405821.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/68b5577ea6ef357e8b8a8cce051b931d_6224331831215248639.png"},"attrs":[["稀有度","A"],["属性","物理"],["特性","强攻"],["阵营","狡兔屋"]],"destiny":[["LEVEL1闪亮登场","[冲刺攻击]或[闪避反击]命中敌人时，比利额外回复2.7点能量，5秒内最多触发一次。「作为反派，你们也太老实了！」「就像为了配合主角登场，故意设置的固定靶一样！」"],["LEVEL2游斗射术","[闪避反击]造成的伤害提升25%；[普通攻击]中的翻滚射击将被视为闪避动作，在招式发动期间获得无敌效果，并且能够触发[极限闪避]，翻滚射击触发[极限闪避]时，将自动街接[闪避反击]。「星徽骑士从来不会逃避危险，那叫做战术游走射击！」 --星徽闪耀-交流区，会员「哦梦中的莫妮卡」留言。"],["LEVEL3星徽骑士的教导","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2绝不懈怠训练，绝不停止进步！这是星徽骑士倡导的精神。比利真的相信，而且真的在遵守。"],["LEVEL4星徽-惩戒弹药","[强化特殊技]命中敌人时，招式的暴击率随目标距离提升；比利与目标距离越近，提升效果越显著，最高提升32%。比利独创的爆破型子弹，在近身战时有着惊人的威力。不过比利改造子弹的本意，是让开枪时的演出效果更加帅气。"],["LEVEL5失落技术构造体","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2未知的精密元件和独特的构造设计带给了比利远超同侪的性能…和维修养护费用。"],["LEVEL6星徽-英雄时刻","累计命中敌人10次或触发[极限闪避]时，比利造成的伤害提升6%，最多叠加5层，被敌方击退或击飞后增益效果重置。「呼~感觉来了！安比你让开，现在是我的主场！」「呃，安比，能帮忙放一下星徽骑士片头曲吗？」 --狡兔屋内部「十大禁句」评选，安比的提案。"]],"skill":[{"title":"普通攻击：火力全开","desc":"点按发动：对前方敌人发动穿透攻击，造成物理伤害；连点或长按时，可以进入蹲姿射击，延长射击的持续时间；蹲姿射击途中拖曳摇杆时，可以发动翻滚射击，调整身位井造成物理伤害； 蹲姿射击途中停止连点或松开长按时，对前方敌人发动终结射击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/06/82232455/cddfb0c1270bdad20980e50ecd3ee4db_4041495098217500327.gif"},{"title":"闪避：好险好险","desc":"点按发动：快速的冲刺闪避； 招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/63e596206aff7758aefe59a310916354_2421833021343239210.gif"},{"title":"冲刺攻击：星-徽-制-裁","desc":"拖曳摇杆发动闪避后，点按发动：向身周大范围区域进行穿透攻击，造成物理伤害。未拖曳摇杆发动闪避后，点按发动：向前方直线区域进行穿透攻击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/248bac4851bb902913577cc6ac120fb1_7452104568839095727.gif"},{"title":"闪避攻击：公平决斗","desc":"触发[极限闪避]后，点按发动：对前方敌人发动穿透攻击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/d83169f16fa34ecbcce25e239a6b3b18_1902346181683387066.gif"},{"title":"快速支援：星徽-同伴之力","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动穿透攻击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/9bc8ef55ea32e7d3a82a769419f78a25_8578291675983305025.gif"},{"title":"回避支援：闪转步伐","desc":"前场角色即将被攻击时，点按发动：回避敌人的攻击，触发[极限视域]；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/fc4a54ca5cbbdb6eb678ffb2c67e732c_3559400544828010653.gif"},{"title":"支援突击：要害射击","desc":"发动[回避支援]后，点按发动：对前方大范围敌人发动穿透攻击，造成物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/3e10e02f7258581909ac2fa407407339_5955527621710852132.gif"},{"title":"特殊技：乖乖站好","desc":"点按发动：向前方直线区域进行至多三段的穿透攻击，造成物理伤害； 招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/38d13aad56328fbfed618bc26a77bc85_6349139750717467100.gif"},{"title":"强化特殊技：清场时间","desc":"能量足够时，点按发动：向前方直线区域进行强力穿透攻击，造成大量物理伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/42b6e2a25fc0b388468436133884a7c6_5892079655130965948.gif"},{"title":"连携技：星徽荣耀幻影","desc":"触发[连携技]时，选择对应角色发动：对身周大范围敌人发动强力穿透攻击，造成大量物理伤害； 招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/df23efe7cf5b38795aaed06d04645915_1038790136550273344.gif"},{"title":"终结技：星徽在此闪耀","desc":"喧响等级达到[极]时，点按发动：对身周大范围敌人发动强力穿透攻击，造成大量物理伤害； 招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/45f2dc15de4043002aa7931f74908ad8_2855978907534008596.gif"},{"title":"核心技","desc":"核心被动:稳定据枪比利在[普通攻击]中进入蹲姿射击，自身造成的伤害提升25%，进行移动、回到待机、被击退或被击飞后增益效果结束。（此处数据为初始数据）额外能力:骑士战队队伍中存在与自身属性或阵营相同的角色时触发: 比利发动[连携技]后，下次发动[终结技]时，招式造成的伤害提升50%，最多叠加2次，发动[终结技]后效果重置。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/04/141353238/55dcff0bb2b5f81132c27190eb2baf4a_934459152253856915.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/比利.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/比利.md"},_=Object.assign(i,{setup(d){return(p,n)=>(e(),c("div",null,[o(t(a))]))}});export{l as __pageData,_ as default};
