import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as o,o as a,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"珂蕾妲","idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":318,"name":"珂蕾妲","summary":"珂蕾妲·贝洛伯格","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/27/82232455/e49ffec1188f11ac70f2fd7e500fbd2c_5771997888796504931.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/3fbcf7ef41cd41e755af9400dee3306c_4635529747293801642.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/1f5232cabbb26b8ab35e9e3b82451983_2887264728262933365.png"},"attrs":[["稀有度","S"],["属性","火"],["特性","击破"],["阵营","白祇重工"]],"destiny":[["LEVEL1锤击节奏","当衔接在[普通攻击]第二、第四段或强化[普通攻击]后，快速发动[特殊技]或[强化特殊技]时，招式造成的失衡值提升15%。火爆的脾气只是表象，其实珂蕾妲心思很细腻。比如她知道什么时候挥锤能打得更痛。"],["LEVEL2动能回收","[强化特殊技]命中敌人时，珂蕾妲回复60点能量，45秒内最多触发一次。对珂蕾妲来说，完美的锤击自有独特的节奏。像是加速敲击手鼓，每一击都在为下一次更强的节拍蓄力。当然，面前的「玩具」能够撑住几下，就不好说了。"],["LEVEL3「别惹我！」","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2作为合格的「玩具」，当珂蕾妲逐渐找到挥锤的手感时，最好不要节外生枝惹麻烦，只要老老实实挨锤就好。——毕竟，反抗又有什么用呢？"],["LEVEL4熔炉余温","消耗[熔炉升温]发动强化[普通攻击]时，珂蕾妲获得1层充能（上限2层）；[连携技]或[终结技]命中敌人时，消耗所有充能，每层充能使当前招式造成的伤害提升18%。当炽热的挥锤让周围温度上升后，珂蕾妲便进入了作战状态。现在，她比以太炸药更易燃易爆。"],["LEVEL5「敢小瞧我？」","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2珂蕾妲非常在意自己这个年轻的首领会让外人小瞧白祇重工。为了弥补这一点，她总是悄悄努力训练。"],["LEVEL6饱和爆破","在[强化特殊技]、[连携技]或[终结技]中，引发爆炸命中敌人时，额外造成珂蕾妲360%攻击力的伤害。在珂蕾妲眼里，讨厌的人分为两种：笨蛋，和无可救药的笨蛋。万幸的是，白祇重工向来不缺烈性以太炸药。多余的份正好可以用在这些笨蛋头上。"]],"skill":[{"title":"普通攻击：砸扁，粉碎","desc":"点按发动：向前方进行至多四段的打击，造成物理伤害；","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/28/82232455/30459c7700eb320c466adde88559c9e4_3553248659761301369.gif"},{"title":"强化普攻","desc":"点按发动：当珂蕾妲拥有[熔炉升温]效果时，能够在第二段攻击后发动强化[普通攻击]，消耗[熔炉升温]效果并造成大量火属性伤害；","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/0149dcc4e71fe613112291f8d2083369_2085554912456784506.gif"},{"title":"普通攻击（协同）","desc":"点按发动：当珂蕾妲与本同时出战时，会由双方配合发动协同攻击，进一步提升招式的威力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/26/73751405/98d48c8a580934ed339f78693e106f7c_4959843774665844133.gif"},{"title":"闪避：走着瞧","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/73751405/e67adb3fbb4101d48980dcdc427dbaaa_3683155346537940761.gif"},{"title":"冲刺攻击：给我颤抖","desc":"闪避时，点按发动：向前方进行打击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/73751405/5e49c32acc73b13bdfaa1a5ad7dbb7c1_5784869668757268983.gif"},{"title":"闪避反击：别小看我","desc":"触发[极限闪避]后，点按发动：向前方敌人发动打击并引发爆炸，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/73751405/0e89e231dbabd786d8d148a353500cac_6269064278675209731.gif"},{"title":"快速支援：让我来","desc":"当前操作中的角色被击飞时，点按发动：向前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/42ff7e830137972aaf65e89c8e1bca4b_8964870539331570243.gif"},{"title":"招架支援：护身锤","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；具有较为优秀的招架性能，承受高强度攻击时[支援点数]的消耗量降低；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/04cbfa7f2ed4e550a0eb928fce414b92_7298169433011100182.gif"},{"title":"支援突击：锤钟","desc":"发动[招架支援]后，点按发动：对前方敌人发动突进打击，造成火属性伤害，并获得[熔炉升温]效果；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/11/25/73751405/a04b4d84be9a5c918f92838bb3e33410_2158377722742618147.gif"},{"title":"特殊技：爆破！铁锤时间","desc":"点按发动：对前方进行打击井引发爆炸，造成火属性伤害；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/f9358a7ba5b639ab79ad677498ce2c33_8778480236893977419.gif"},{"title":"强化特殊技：沸腾熔炉","desc":"能量足够时，点按发动：对前方进行打击并引发强力爆炸，造成大量火属性伤害，并获得[熔炉升温]效果；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/3f0587fb834165b32b46043307029bb5_7992197450550736222.gif"},{"title":"普通特殊技（快速引爆）","desc":"点按发动：若衔接在[普通攻击]第二、第四段或强化[普通攻击]后，则能够快速发动招式，直接引发爆炸；（gif展示第二段）","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/e4c16d3b89e40630f665b986cc370eea_3808574065148902805.gif"},{"title":"强化特殊技（快速引爆）","desc":"能量足够时，点按发动：若衔接在[普通攻击]第二、第四段或强化[普通攻击]后，则能够快速发动招式，直接引发爆炸；（gif展示第二段）","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/6c9c3db6a585b612024dfc60b12980a9_2728472371890353060.gif"},{"title":"普通特殊技（协同）","desc":"点按发动：当珂蕾妲与本同时出战，并衔接在强化[普通攻击]后快速发动招式时，会由双方配会发动协同攻击，进一步提升招式的威力；","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/0e80033682b953e715c5eb91c0bb190e_4050391122777873335.gif"},{"title":"强化特殊技（协同）","desc":"能量足够时，点按发动：当珂蕾妲与本同时出战，并衔接在强化[普通攻击]后快速发动招式时，会由双方配会发动协同攻击，进一步提升招式的威力；","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/7130b8e877a19bf7f8781f3acde93134_817245420128210889.gif"},{"title":"连携技：天崩-地裂","desc":"触发[连携技]时，选择对应角色发动：在前方大范围区域发动强力爆炸，造成大量火属性伤害，并获得[熔炉升温]效果；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/cc75e14c1e4f99f1a7c007fe0b9d2c3c_8795367504443909916.gif"},{"title":"终结技：锤进地心","desc":"喧响等级达到[极]时，点按发动：在前方大范围区域发动强力爆炸，造成大量火属性伤害，并获得[熔炉升温]效果；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/c3507b8a06937786b0b8ba68614b170e_5611621119473070702.gif"},{"title":"终结技（协同）","desc":"喧响等级达到[极]时，点按发动：当珂蕾妲与本同时出战时，会由双方配合发动协同攻击，进一步提升招式的威力；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/524f7c7866f698a6d613b5de05561021_6813108104540600980.gif"},{"title":"","desc":"核心被动：爆破锤珂蕾妲发动[强化特殊技]，或消耗[熔炉升温]发动强化[普通攻击]时，招式造成的失衡值提升30%。（此处数据为初始数据）额外能力：白祇管理学队伍中存在与自身属性或阵营相同的角色时触发：珂蕾妲发动[强化特殊技]引发爆炸命中敌人时，对目标施加减益效果；当目标陷入失衡状态后，全队[连携技]对其造成的伤害提升35%，最多叠加2层，失衡状态结束时该效果重置。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/73751405/66cf38ce8856c7c4901a908855382f25_8531634153223895212.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/珂蕾妲.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/珂蕾妲.md"},h=Object.assign(i,{setup(d){return(p,n)=>(a(),o("div",null,[e(t(c))]))}});export{l as __pageData,h as default};
