import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as a,o,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"艾莲","idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":317,"name":"艾莲","summary":"艾莲·乔","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/05/28/160132993/b7f945843942bc32064f240317ae7beb_617780162075671896.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/48fe327785e9d04c9257c6d55a3fcc49_8350380198191067077.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/25/76099754/a972a1d8d91df313f43a2bc26e1d1342_5637677391158651824.png"},"attrs":[["稀有度","S"],["属性","冰"],["特性","强攻"],["阵营","维多利亚家政"]],"destiny":[["LEVEL1凛冬前兆","在[冲刺攻击：冰渊潜袭]中发动快速剪击命中敌人时，获得的[急冻充能]提升至3点；在[冲刺攻击：冰渊潜袭]中发动蓄力剪击命中敌人时，获得的[急冻充能]提升至6点；每消耗1点[急冻充能]，艾莲的暴击率提升2%，持续15秒，最多叠加6层，每层效果单独结算持续时间。寒风是凛冬的前兆，也是艾莲请长假冬眠的信号。话说回来，鲨鱼真的需要冬眠吗？"],["LEVEL2冰洋掠食者","发动第三段[普通攻击：急冻修剪法]后，可以直接衔接发动[强化特殊技：鲨卷风]；发动[强化特殊技]时，每拥有1点[急冻充能]，当前招式造成的暴击伤害提升20%，最多提升60%。虽然沾染鲜红，艾莲所持的巨剪并无半分温热。彻骨的寒意来自铁铸的凶器，也来自面对捕食者的恐慌。"],["LEVEL3永不加班","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「效率」的意义就是今天可以早点回家睡觉。所以要更快地斩击，更用力地出刀。"],["LEVEL4无休寒潮","队伍中任意角色对敌人施加[冻结]效果或使敌人失衡时，自身获得6点[急冻充能]和[快蓄]效果，并回复4点能量，能量回复效果10秒内最多触发一次。凌冽的攻势不曾止休，可怖的造物们得以在艾莲的刀下领受冬的美与残酷。不过相比起来，还是作为女仆节假日无休的工作更加残酷..."],["LEVEL5睡眠充足","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]、技能等级+2除非是最艰难的战斗中，艾莲永远都是一副「如果不是在工作我立刻深度睡眠」的样子。因此掉以轻心而完蛋的敌人不在少数。"],["LEVEL6盛宴时分","发动[强化特殊技]、[连携技]或获得[快蓄]时，艾莲的穿透率提升20%，持续6秒，同时获得1层[盛宴]，[盛宴]最多叠加3层；拥有3层[盛宴]后，在[冲刺攻击：冰渊潜袭]中发动蓄力剪击命中敌人时，消耗所有[盛宴]，使当前招式造成的伤害提升250%。一道伤口，一滴鲜血。一次狩猎，一场盛宴。一纸薄命，一刻满足。"]],"skill":[{"title":"普通攻击：利齿修剪法","desc":"点按发动：向前方进行至多三段的攻击，造成物理伤害；第三段攻击期间，连点或长按可以延长招式的持续时间。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/5a0246760551ca9316f2eb94723eb4af_4707129995717062240.gif"},{"title":"普通攻击：急冻修剪法","desc":"激活[急冻]效果后，点按发动：向前方进行至多三段的攻击，造成冰属性伤害；每段攻击消耗1点[急冻充能]；第三段攻击期间，连点或长按可以延长招式的持续时间。  ","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/b97ca69946b48de0814537fbefa6d2e3_3255328956530209940.gif"},{"title":"闪避：涡漩","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果；触发[极限闪避]时，艾莲将获得[快蓄]效果，缩短[冲刺攻击：冰渊潜袭]的蓄力时间，最多持续5秒或触发一次。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/51011d3933586858cb3a7cb191c8806a_3066659318617704482.gif"},{"title":"冲刺：狩猎巡游","desc":"长按或双击发动：进入[巡游]状态，快速冲刺移动；在[巡游]状态下使用摇杆控制方向，松开摇杆则会停下；招式发动瞬间拥有短暂的无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/2d2f1ee5ec291c5f6104aceb58e245ef_2243953294080626284.gif"},{"title":"冲刺攻击：冰渊潜袭","desc":"在[巡游]状态下，点按发动：对敌人进行回旋斩击，随后拉开剪刀，发动快速剪击，造成冰属性伤害；快速剪击命中时，获得1点[急冻充能]。在[巡游]状态下，长按发动：对敌人进行回旋斩击，随后拉开剪刀，发动蓄力剪击，造成大量冰属性伤害；蓄力剪击命中时，获得3点[急冻充能]，若敌人处于[冻结]状态下，则必定触发[碎冰]效果；招式蓄力期间抗打断等级提升，受到的伤害降低40%；招式发动期间拥有短暂的无敌效果。急冻拥有[急冻充能]时，将激活[急冻]效果；激活[急冻]效果后，[普通攻击]和[冲刺攻击]将消耗[急冻充能]，造成大量冰属性伤害；艾莲最多可以拥有6点[急冻充能]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/f49423befbccf1209c4f0f18cf5c051c_4654494953684204575.gif"},{"title":"冲刺攻击：骇浪","desc":"闪避时，点按发动：向前方进行突进斩击，造成物理伤害；","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/b15a9658dfb6b41f834504953cbfef03_2735767880775926722.gif"},{"title":"冲刺攻击：寒潮","desc":"激活[急冻]效果后，闪避时，点按发动：向前方进行突进斩击，造成冰属性伤害；消耗1点[急冻充能]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/109a50a3f66b777b8b7d5ba9aeed5101_4853862863303098081.gif"},{"title":"闪避反击：暗礁","desc":"触发[极限闪避]后，点按发动：快速穿过敌人，拉开剪刀进行剪击，造成冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/d5ab647339d130c0afdbd870576eb96b_7890053651522979190.gif"},{"title":"快速支援：护卫鲛","desc":"当前操作中的角色被击飞时，点按发动：快速穿过敌人，拉开剪刀进行剪击，造成冰属性伤害；招式发动期间拥有无敌效果。当前操作中的角色被击飞时，拖曳摇杆，点按发动：进入[巡游]状态，快速冲刺移动；在[巡游]状态下使用摇杆控制方向，松开摇杆则会停下；招式发动瞬间拥有短暂的无敌效果。发动[快速支援]时，艾莲将获得[快蓄]效果，缩短[冲刺攻击：冰渊潜袭]的蓄力时间，最多持续5秒或触发一次。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/f08e92f222ea3d26977b6cf28920f89f_8936852358220620077.gif"},{"title":"招架支援:迎头浪","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/59dcbb4ae175fbd57c4124d2a53fb1e7_2237436398612156469.gif"},{"title":"支援突击：巡洋鲨","desc":"发动[招架支援]后，点按发动：对前方敌人发动突进斩击，造成冰属性伤害；招式攻击期间，连点或长按可以延长招式的持续时间；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/8491fc5f9c73c754ab45d3192465cd0d_8185479710475258767.gif"},{"title":"特殊技：摆尾","desc":"点按发动：向前方进行快速甩尾攻击，造成冰属性伤害；招式发动期间抗打断等级提升。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/ee1826c91e8ef03a4d5dc6eb284f8452_530156163402146980.gif"},{"title":"强化特殊技：横扫","desc":"能量足够时，点按发动：向前方进行强力甩尾攻击，造成大量冰属性伤害；命中敌人时，获得1点[急冻充能]；招式发动期间拥有无敌效果；招式发动后，可直接衔接第三段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/fd282fa7a01523c6c0e495e8f7ec4723_1973303145788414214.gif"},{"title":"强化特殊技：鲨卷风","desc":"[强化特殊技：横扫]发动后，能量足够时，点按发动：向前方进行强力连续斩击，造成大量冰属性伤害；命中敌人时，获得1点[急冻充能]；招式发动期间拥有无敌效果；招式发动后，可直接衔接第三段[普通攻击]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/900592b01c98a029310dd66449baa159_1489003142069485017.gif"},{"title":"连携技：雪崩","desc":"触发[连携技]时，选择对应角色发动：从空中下砸并生成一阵冰风暴，造成大量冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/08d46ee49e02d60816e80e14a601e7d7_6839569704144049416.gif"},{"title":"终结技：永冬狂宴","desc":"喧响等级达到[极]时，点按发动：从空中下砸并生成一阵冰风暴，随后使用剪刀进行强力一击，造成大量冰属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/15559334/55da4ae5fab2ae5e20c1dfb6a48531fa_6873287736043647622.gif"},{"title":"核心技","desc":"核心被动：凌牙厉齿艾莲在[冲刺攻击：冰渊潜袭]中发动蓄力剪击，或消耗[急冻充能]发动[普通攻击：急冻修剪法]时，招式的暴击伤害提升50%。（此处数据为初始数据）额外能力：风暴潮队伍中存在与自身属性或阵营相同的角色时触发：艾莲造成冰属性伤害时，后继冰属性伤害提升3%，持续10秒，最多叠加10层，重复触发时刷新持续时间。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/03/4859366/cbffbfd0a28b39ae1e4cfa33f957485c_3061803503288547780.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/艾莲.md","filePath":"game/zzz/role/[pkg].md"}'),i={name:"game/zzz/role/艾莲.md"},h=Object.assign(i,{setup(d){return(p,n)=>(o(),a("div",null,[e(t(c))]))}});export{l as __pageData,h as default};
