import{_ as c}from"./chunks/index.VAya1KE4.js";import{c as o,o as a,G as e,k as t}from"./chunks/framework.BFEalnvt.js";import"./chunks/theme.B1I7hsVa.js";import"./chunks/getArrayRandomItem.LhrzGfRF.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"params":{"pkg":"露西","idList":[1158,1109,996,997,950,916,840,801,759,680,634,317,65,378,73,316,318,150,758,493,485,80,371,158,227,379,179,147],"data":{"id":493,"name":"露西","summary":"露西亚娜·德·蒙特夫","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/01/34600917/3fa01447d481351416ef664e21894c29_801262856017991825.png","avatar":{"m":"https://act-upload.mihoyo.com/nap-obc-indep/2024/12/30/76099754/9b480cb820e3bee5981097b7d1bdd76d_8832919689621293149.png","pc":"https://act-upload.mihoyo.com/nap-obc-indep/2024/07/01/34600917/ee0ad3abe4f3d6019dad2d7d1d183e84_4132005836743683615.png"},"attrs":[["稀有度","A"],["属性","火"],["特性","支援"],["阵营","卡吕冬之子"]],"destiny":[["LEVEL1队形操练中","亲卫队小猪发动[亲卫队小猪：回旋挥击！]时，为露西回复2点能量，15秒内最多触发一次，每只亲卫队小猪单独结算冷却时间。「阿草，向右看齐！···那是左边，笨蛋！！！」一声令下，两种方向，三阵惨呼，四面哀嚎。"],["LEVEL2小猪大队长","露西发动[连携技]或[终结技]时，将为所有友方角色、邦布和亲卫队小猪施加[加油！]状态，持续10秒。「毋庸置疑，露西队长是卡吕冬野猪希人最棒的领导者！」这是阿木在「卡吕冬之子年度工作报告」中的结语。"],["LEVEL3铁腕监护人","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2「要是露西队长在打飞我们的时候下手轻点就更好了。」这是阿木没敢说出来的后半句心里话。"],["LEVEL4无规则球赛","处于[加油！]状态下的友方单位，暴击伤害额外提升10%。规则和裁判的肋骨，总有一样要被打破。"],["LEVEL5荒原贵公主","[普通攻击]、[闪避]、[支援技]、[特殊技]、[连携技]技能等级+2对球棍下惨叫的对手用敬语，向匍匐在脚边的敌人说抱歉，这就是千金的完美礼仪。"],["LEVEL6獠牙堪烈火","处于[加油！]状态下的队友发动[强化特殊技]命中敌人时，一只亲卫队小猪会从空中落地引发爆炸，造成等同于亲卫队小猪300%攻击力的火属性伤害，并使全队[加油！]状态的持续时间提升5秒；持续时间最多提升3次，上限30秒，重新获得[加油！]状态时刷新可提升次数；亲卫队小猪落地引发爆炸后，将会发动一次[亲卫队小猪：回旋挥击！]。被称为「赤鬣亲卫队」的小猪希人们总是冲锋在战斗的最前线！虽然说不清从球棒起飞的那一刹那，心绪是万念俱灰还是心甘情愿。勇敢小猪，身先士卒！"]],"skill":[{"title":"普通攻击：淑女的球棍","desc":"点按发动：向前方进行至多四段的打击，造成物理伤害和火属性伤害；当露西发动[普通攻击]第四段时，场上的亲卫队小猪会一起发动[亲卫队小猪：回旋挥击！]。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/30/34600917/3c35247b07c4aca6caea59564ad411fa_4303141090997032153.gif"},{"title":"亲卫队小猪：抄家伙！","desc":"战斗中，亲卫队小猪会随机使用棒球棍、拳套、弹弓，自动对敌人发动攻击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/24/82500917/31524315bd711655472da61259d559f3_4094532742237643187.gif"},{"title":"亲卫队小猪：回旋挥击！","desc":"当露西发动[普通攻击]第四段，或自身获得[加油！]状态时，亲卫队小猪会挥舞棒球棍持续转圈，对周围敌人造成物理伤害。若无法立即发动，亲卫队小猪会记住所需发动次数。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/9b04eae3ffdc9b1aa9bed219c100f529_8595997568231396020.gif"},{"title":"闪避：坏球！","desc":"点按发动：快速的冲刺闪避；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/9e500662b8cb49f279c0f7e55b93751d_1058231604093133841.gif"},{"title":"冲刺攻击：豪勇猪突！","desc":"闪避时，点按发动：向前方进行快速打击，造成物理伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/82500917/2f8528394409a5931b2708438dd6f1ed_7483526736039681576.gif"},{"title":"闪避反击：獠牙折转！","desc":"极限闪避时，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/82500917/edf16759ff98c739f96dcc98560115f9_3639386103346109034.gif"},{"title":"快速支援：触身球！","desc":"当前操作中的角色被击飞时，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/55d7887546d6e52b806bf86da517d28b_9169671070643596994.gif"},{"title":"招架支援：安全上垒！","desc":"前场角色即将被攻击时，点按发动：招架敌人的攻击，累积大量失衡值；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/22/82500917/ae8a20eb30b7e306180acfb69e948662_6159545282726773663.gif"},{"title":"支援突击：触垒得分！","desc":"发动[招架支援]后，点按发动：对前方敌人发动打击，造成火属性伤害；招式发动期间拥有无敌效果。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/c12121b169176182900fbdfcff4d358d_5991760537660411597.gif"},{"title":"特殊技：安打！","desc":"点按发动：将亲卫队小猪召回并击出[平直球]，亲卫队小猪命中敌人或墙璧时引发爆炸，造成火属性伤害。长按发动：将亲卫队小猪召回并击出[高飞球]，亲卫队小猪从空中落地时引发爆炸，造成火属性伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/30/34600917/9e15bf6b8d32ca181cfdbb21f88ea679_996864792037134487.gif"},{"title":"强化特殊技：全垒打！","desc":"能量足够时，点按发动：将亲卫队小猪召回并击出[平直球]，亲卫队小猪命中敌人或墙壁时引发爆炸，造成大量火属性伤害;招式发动期间拥有无敌效果。招式命中敌人时，将触发[快速支援]。能量足够时，长按发动：将亲卫队小猪召回并击出[高飞球]，亲卫队小猪从空中落地时引发爆炸，造成大量火属性伤害;招式发动期间拥有无敌效果。招式命中敌人时，将触发[快速支援]。加油!发动[强化特殊技]后，亲卫队小猪引发爆炸时，将为所有友方角色、邦布和亲卫队小猪施加[加油！]状态。[加油！]状态下，友方单位的攻击力提升，提升数值等同于露西13.8%初始攻击力+44点，最高不超过600点。击出[平直球]时，[加油！]状态持续10秒，击出[高飞球]时，[加油！]状态持续15秒，重复触发时刷新持续时间。（此处数据为初始数据）","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/88237bc77bac48c1b05b484d8fb90ab6_3076318588309161033.gif"},{"title":"连携技：大满贯！","desc":"触发[连携技]时，选择对应角色发动：露西起跳将场上亲卫队小猪打出，对前方大范围敌人造成大量火属性伤害；招式发动期间拥有无敌效果。招式命中敌人时，将触发[快速支援]","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/033205217ed684363957afecb3c921b0_5098294047738448924.gif"},{"title":"终结技：再见全垒打！","desc":"喧响等级达到[极]时，点按发动：将所有亲卫队小猪依次打出，对前方大范围敌人造成大量火属性伤害；招式发动时，队伍中其他角色回复10点能量，下一名换入前场的角色额外回复20点能量；招式发动期间拥有无敌效果。招式命中敌人时，将触发[快速支援]","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/08/23/82500917/090fdc942c3f24575ac7ba0ff7f45241_6353835608073155667.gif"},{"title":"核心技","desc":"核心被动：赤鬣亲卫队当露西发动[特殊技]或[强化特殊技]时，亲卫队小猪会加入战斗并呼叫出另外两只亲卫队小猪；亲卫队小猪可以继承露西的攻击力、冲击力和异常精通；[加油！]对亲卫队小猪的增益效果提升至原本的140%，并使亲卫队小猪的攻击频率提升。（此处数据为初始数据）额外能力：击「球」准备！队伍中存在与自身属性或阵营相同的角色时触发：亲卫队小猪可以继承露西的暴击率和暴击伤害。","icon":"https://act-upload.mihoyo.com/nap-obc-indep/2024/06/30/34600917/84d5c6d10d7cd7ad8fc98c6f2012583f_4481597023540337364.gif"}]},"favModalLocalKey":"vitepress_game_zzz_role_fav_modal","favLocalKey":"vitepress_game_zzz_role_fav"},"relativePath":"game/zzz/role/露西.md","filePath":"game/zzz/role/[pkg].md"}'),d={name:"game/zzz/role/露西.md"},_=Object.assign(d,{setup(i){return(p,n)=>(a(),o("div",null,[e(t(c))]))}});export{l as __pageData,_ as default};
